INSERT INTO `content` (`conid`, `contitle`, `subid`, `contxt`, `contime`) VALUES
(11, 'ยุคที่ 1 ปี ค.ศ. 1951-1958', 'ง33103', 'ก่อนหน้าปี 1951 เครื่องคอมพิวเตอร์จะมีใช้เฉพาะนักวิทยาศาสตร์ วิศวกร และทหารเท่านั้น จนกระทั่งผู้สร้าง ENIAC คือ Mauchly และ Eckert ได้จัดตั้งบริษัทเพื่อทำตลาดเชิงพาณิชย์ของเครื่องรุ่นถัดมาของพวกเขา คือเครื่อง UNIVAC ซึ่งคอมพิวเตอร์ในยุคนี้จะมี หลอดสูญญากาศ และ ดรัมแม่เหล็ก (magnetic drum) เป็นส่วนประกอบสำคัญ แต่หลอดสุญญากาศจะมีไม่น่าเชื่อถือสูง เป็นเหตุให้ต้องใช้ความพยายามอย่างมากในการทำให้เครื่องในยุคนั้นสามารถทำงานได้ ส่วนดรัมแม่เหล็กถูกใช้เป็นหน่วยความจำหลัก (primary memory) บนเครื่องคอมพิวเตอร์ส่วนมากในยุคแรกนี้ ส่วนหน่วยบันทึกข้อมูลสำรอง (secondary storage) ซึ่งใช้เก็บทั้งข้อมูลและคำสั่งโปรแกรมในยุคนี้จะอยู่ในบัตรเจารู จนปลายยุคนี้เทปแม่เหล็กจึงได้ถูกนำมาใช้เป็นหน่วยบันทึกข้อมูลสำรอง\r\nภาษาคอมพิวเตอร์ในยุคนี้จะอยู่ในรูปของภาษาเครื่อง ซึ่งเป็นตัวเลขฐาน 2 ทั้งสิ้น ทำให้ผู้ที่จะสามารถโปรแกรมให้เครื่องทำงานได้ ต้องเป็นผู้เชี่ยวชาญเท่านั้น \r\n', '0000-00-00'),
(12, 'ยุคที่ 2   ปี ค.ศ. 1954-1962', 'ง33103', '<p>\r\n\r\n</p><ul><li>การพัฒนาที่สำคัญที่สุดที่แบ่งแยกยุคนี้ออกจากยุคแรก\r\nคือการแทนที่หลอดสูญญากาศด้วยทรานซิสเตอร์ (transistor) หน่วยความจำพื้นฐานก็ได้มีการพัฒนามาเป็น\r\nmagnetic\r\ncore รวมทั้งมีการใช้\r\nmagnetic\r\ndisk ซึ่งเป็นหน่วยบันทึกข้อมูลสำรองที่มีความเร็วสูงขึ้น\r\nนอกจากนี้ ส่วนประกอบที่คอมพิวเตอร์ได้ถูกรวบรวมเข้าไว้ใน แผ่นวงจรพิมพ์ลาย (printed\r\ncircuit boards) ซึ่งง่ายต่อการเปลี่ยนและมีการสร้างโปรแกรมวิเคราะห์เพื่อหาส่วนผิดพลาดได้อย่างรวดเร็ว</li></ul><p>\r\n\r\n</p><ul><li>ภาษาโปรแกรมระดับสูง เช่น FORTRAN\r\nและ\r\nCOBOL\r\nได้ถูกใช้ในการโปรแกรมสำหรับยุคนี้\r\nโปรแกรมเมอร์สามารถใช้งานภาษาเหล่านี้ได้สะดวกกว่าคอมพิวเตอร์ในยุคที่ 1\r\nเนื่องจากมีไวยากรณ์ที่คล้ายคลืงกับภาษาอังกฤษ อย่างไรก็ดี\r\nเนื่องจากคอมพิวเตอร์สามารถทำงานได้แต่เฉพาะกับภาษาเครื่อง\r\nทำให้ต้องใช้โปรแกรมตัวอื่น คือ compiler และ interpreter\r\nในการแปลงภาษาระดับสูงให้เป็นภาษาเครื่อง</li></ul><p>\r\n\r\n</p><ul><li>ในยุคที่ 2\r\nเริ่มมีการติดต่อสื่อสารระหว่างคอมพิวเตอร์ 2\r\nเครื่องที่อยู่ห่างกันโดยผ่านสายโทรศัพท์ ถึงแม้ว่าจะติดสื่อสารกันได้ช้ามากก็ตาม\r\nปัญหาในยุคนี้คืออุปกรณ์รับข้อมูลและอุปกรณ์แสดงผลทำงานได้ช้ามาก\r\nทำให้คอมพิวเตอร์ต้องรอการรับข้อมูลหรือการแสดงผลบ่อย ๆ ซึ่ง Dr.Daniel\r\nSlotnick ได้ทำการพัฒนาเพิ่มเติม\r\nโดยใช้หลักการของการประมวลผลแบบขนานกัน นอกจากนั้ยังมีกลุ่มคณาจารย์และนักเรียกจาก\r\nMassachusetts\r\nInstiute of Technoligy พัฒนาระบบ มัลติโปรแกรมมิ่ง (multiprogramming)\r\nซึ่งเป็นการจัดสรรให้คอมพิวเตอร์ทำงานหลายโปรแกรมพร้อม\r\nๆ กนได้ ทำให้ไม่ต้องเสียเวลารอหน่วยรับข้อมูลและหน่วยแสดงผลอีกต่อไป</li></ul><p>\r\n\r\n<br></p>', '0000-00-00'),
(13, 'ยุคที่ 3  ปี ค.ศ. 1963-1972', 'ง33103', '<p>\r\n\r\n</p><ul><li>ในยุคที่\r\n3\r\nเป็นยุคของอุตสาหกรรมคอมพิวเตอร์ที่มีการเติบโตมาก ได้มีการนำ แผงวงจรรวม (IC\r\nหรือ\r\nintegrated\r\ncircuits) ซึ่งประกอบด้วยทรานซิสเตอร์และวงจรไฟฟ้าที่รวอยู่บนแผ่นซิลิกอนเล็ก\r\nๆ มาแทนการประกอบแผ่นวงจรพิมพ์ลาย\r\nทำให้เวลาการทำงานขิงคอมพิวเตอร์ลดลงอยู่ในหน่วยหนึ่งส่วนพันล้านวินาที นอกจากนี้\r\nมินิคอมพิวเตอร์ได้ถือกำเนิดขึ้นในปี ค.ศ.1965\r\nคือเครื่อง PDP-8 ของ Digital\r\nEquipment Corportion (DEC) ซึ่งต่อมาก็มีการใช้มินิคอมพิวเตอร์ที่สามารถติดต่อกับคอมพิวเตอร์กันอย่างแผร่หลาย\r\nรวมทั้งมีการใช้งาน เทอร์มินัล (terminal) ซึ่งเป็นจอคอมพิวเตอร์ผ่านทาง\r\nคีย์บอร์ด (keyboard)\r\nทำให้การป้อนข้อมูลและพัฒนาโปรแกรมกระทำได้สะดวกขึ้น\r\n</li></ul><p>\r\n\r\n</p><ul><li>ภาษาโปรแกรมระดับสูงได้เกิดขึ้นมากมานในยุคที่\r\n3 เช่น\r\nRPG\r\nAPL BASICA เป็นต้น\r\nและได้มีการเปิดตัว โปรแกรมจัดการระบบ (Operating system) ซึ่งช่วยให้สามารถบริการทรัพยากรของคอมพิวเตอร์ได้อย่างมีประสิทธิภาพ\r\nนอกจากนี้ ระบบแบ่งเวลา (time sharing) ก็ทำให้สามารถติดต่อเทอร์มินัลจำนวนมากเข้าไปยังคอมพิวเตอร์\r\n1\r\nเครื่อง โดยที่ผู้ใช้แต่ละคนสามารถทำงานในส่วนของตนได้พร้อม ๆ กัน</li></ul><p>\r\n\r\n<br></p>', '0000-00-00'),
(14, 'ยุคที่ 4 ปี ค.ศ. 1972-1984', 'ง33103', '<p>\r\n\r\n</p><ul><li>ในยุคที่\r\n4\r\nเทคโนโลยีแผงวงจรรวมได้พัฒนาขึ้นเป็น แผงวงจรรวมขนาดใหญ่ (LSI\r\nหรือ\r\nlarge-scale\r\nintegartion) และจากนั้นก็มีการพัฒนาต่าเป็น\r\nแผงวงจรขนาดใหญ่มาก (Very Large-Scale integartion - VLSI) ซึ่งทำให้เกิด\r\nmicroprocessor\r\nตัวโลกของโลก\r\nคือ Intel\r\n4004\r\nจากบริษัท Intel\r\nซึ่งเป็นการใช้แผ่นชิฟเพียงแผ่นเดียวสำหรับเก็บ\r\nหน่วยควบคุม (control\r\nunit) และ\r\nคำนวณเลขตรรกะ (arithmetic-logic unit) ของคอมพิวเตอร์ทั้งหมดเทคนิคในการย่อทรานซีสเตอร์ให้อยู่กันอย่างหนาแน่นบนแผ่นซิลิกอนนี้\r\nได้รับการพัฒนาอย่างต่อเนื่องจากปัจจุบันสามารถเก็บทรานซิสเตอร์นับล้านตัวไว้ในชิปเพียงหนึ่งแผ่น\r\nในส่วนของหน่วยบันทึกข้อมูลสำรอง (secondary storage) ก็ได้เพิ่มความจุขึ้นอย่างมากจนสามารถเก็บข้อมูลนับพันล้านตัวอักษรได้ในแผ่นดิสก์ขนาด\r\n3 นิ้ว</li></ul><p>\r\n\r\n</p><ul><li>เนื่องจากการเพิ่มความจุของหน่วยบันทึกข้อมูลสำรองนี่เอง\r\nซอฟต์แวร์ชนิดใหม่ได้พัฒนาขึ้น\r\nเพื่อให้สามารถเก็บรวมรวบและบันทึกแก้ไขข้อมูลจำนวณมหาศาลที่ถูกจัดเก็บไว้ นั่นคือ\r\nซอฟร์แวร์ ฐานข้อมูล (Data base ) นอกจากนี้\r\nยังมีการถือกำเนิดขึ้นของเครื่องคอมพิวเตอร์ส่วนบุคคลในปี 1975\r\nคือเครื่อง Altair\r\nซึ่งใช้ชิป\r\nintel\r\n8080\r\nและถัดจากนั้นก็เป็นยุคของเครื่อง และ ตามลำดับ\r\nในส่วนของซอฟต์แวร์ก็ได้มีการพัฒนาให้เป็นมิตรกับผู้ใช้\r\nมีขนาดใหญ่และซับซ้อนมากขึ้นเรื่อย ๆ รวมทั้งมีการนำเทคนิคต่าง ๆ เช่น OOP\r\n(Object-Oriented Programming) และ Visual\r\nProgramming มาเป็นเครื่องมือช่วยในการพัฒนา</li></ul><ul><li>\r\n\r\nการพัฒนาที่สำคัญอื่นๆในยุคที่ 4\r\nคือการพัฒนาเครื่องข่ายคอมพิวเตอร์ความเร็วสูง\r\nทำให้คอมพิวเตอร์สามารถเชื่อมโยงและแลกเปลี่ยนกันได้ โดยการใช้งานภายในองค์กรนั้น\r\nระบบเครื่อข่ายท้องถิ่น (Local\r\nAraa Networks) ซึ่งนิยมเรียกว่า แลน (LANs) จะมีบทบาทในการเชื่องโยงเครื่องนับร้อยเข้าด้วยกันในพื้นที่ไท่ห่าวกันนัก\r\nส่วนระบบเครื่องข่ายระยะไกล ( Wide\r\nArea Networks ) หรือ แวน (WANs) จะทำหน้าที่เชื่อมโยงเครื่องคอมพิวเตอร์ที่อยู่ห่างไกลคนละซีกโลกเข้าด้วยกัน\r\n<br></li></ul>', '0000-00-00'),
(15, 'ยุคที่ 5   ปี ค.ศ. 1984-1990', 'ง33103', '<p>\r\n\r\n</p><ul><li>การพัฒนาคอมพิวเตอร์ในยุคนี้เน้นในด้านการประมวลผลแบบขนาน\r\n(parallel\r\nprocessing) โดยมองเห็นรูปแบบการที่มีตัวประมวลผลหลาย\r\nๆ ตัวช่วยกันประมวลผลพร้อมกันเพื่อเพิ่มความเร็ว<span>&nbsp;\r\nการพัฒนาด้านเครือข่ายคอมพิวเตอร์ ทั้งเครือข่ายระยะไกล (</span>WAN:\r\nWide Area Network) และเครื่อข่ายระยะใกล้ (LAN: Local\r\nArea Network) เป็นไปอย่างรวดเร็ว\r\n</li></ul><p>\r\n\r\n</p><ul><li>นอกจากนี้ได้มีเทคโนโลยี RISC\r\n(reduced instruction set) เป็นสถาปัตยกรรมของไมโครโพรเซสเซอร์ที่ใช้คำสั่งสั้นและเป็นพื้นฐานกว่า\r\nCISC\r\n(complex instruction set computing) โดยทำให้ความเร็วเพิ่มขึ้นด้วย</li></ul><p>\r\n\r\n<br></p>', '0000-00-00'),
(16, 'ประวัติกีฬาฟุตซอลต่างประเทศ', 'พ31101', '<p>\r\n\r\n<p>\r\n\r\n</p></p><ul><li>ต้นกำเนิดของฟุตซอล<span>&nbsp; มาจากการเล่นฟุตบอลแบบข้างละ 5 คน\r\nสามารถมองย้อนไปในปี 1930 ที่มอนเตวิโอ ประเทศอุรุกวัย เมื่อฮวน คาร์ลอส\r\nเซอเรียนี&nbsp; ได้นำฟุตบอลข้างละ 5 คนนี้ไปใช้ในในการแข่งขันที่สมาคม\r\n</span>YMCA\r\n<span>ได้เล่นโดยใช้พื้นที่ของสนามบาสเกตบอลในการเล่น\r\nทั้งภายในและภายนอกโรงยิมจึงทำให้เกิดการเล่นแบบอินดอร์ซอคเกอร์ซึ่งได้รับความสนใจอย่างมากเพราะไม่มีปัญหาด้านสภาพภูมิอากาศ\r\nอินดอร์ซอคเกอร์ ได้รับความนิยมและมีคนให้ความสนใจเพิ่มขึ้นเรื่อยๆ\r\nส่วนในประเทศบราซิลมีการกล่าวถึงการพัฒนาฟุตซอลมาจากฟุตบอลข้างถนน กระทั่งปี ค.ศ.\r\n1932 โรเจอร์ เกรน\r\nได้บัญญัติกฎที่เป็นมาตรฐานในการควบคุมการแข่งขันขึ้นและได้ใช้กฎและข้อบังคับนี้ใช้จนมาถึงปัจจุบันนี้\r\nคำว่าฟุตซอล (Futsal) คือ คำที่นานาประเทศใช้เรียกกีฬาประเภทนี้โดยมีรากศัพท์มาจากประเทศสเปนหรือโปตุเกส\r\nที่เรียก soccer&nbsp; ว่า\r\n“FUTbol” หรือ “FUTebol” และที่ภาษาฝรั่งเศสหรือสเปนเรียก\r\nIndoor ว่า ” SALa” ที่แปลว่า อินดอร์\r\nหรือในร่ม ซึ่งรวมเป็นคำว่า “fUtSAl” แต่เป็นคำที่มีฐานจากประเทศสหรัฐอเมริกาโดยเฉพาะ\r\n“fUtSAl” (USA) คำที่เป็นชื่อของประเทศสหรัฐอเมริกา\r\nจึงมีการใช้คำว่า FUTSAL แทน ซึ่งหมายถึง\r\nการเตะบอลในสนามขนาดย่อมในร่ม กลายเป็นคำที่เรียกขานกันแทนคำว่า “Five-A-Side”\r\nหรือบอล 5 คน\r\nนอกจากนั้นการเล่นฟุตซอลกลางแจ้งเมื่อเข้าสู่ช่วงฤดูหนาว\r\nประเทศในบางทวีปของโลกที่ประสบกับปัญหาหิมะตกและสภาพอากาศที่หนาวมากไม่สามารถจัดการแข่งขันกีฬากลางแจ้ง\r\nได้ จึงถือเป็นช่วงสิ้นสุดฤดูกาลแข่งขันแต่เนื่อง\r\nจากฤดูหนาวมีระยะเวลาที่ยาวนานและสภาพอากาศกลางแจ้งไม่เอื้ออำนวยต่อการเล่นกีฬาฟุตบอล\r\nจึงเป็นปัจจัยหนึ่งที่ทำให้คนหันมาเล่นกีฬาในร่มแทน และนี่คือที่มาของกีฬาฟุตบอลในร่ม\r\n5 คน หรือที่เรียกว่า “ ฟุตซอล” (FUTSAL)</span></li></ul><p><p>\r\n\r\n\r\n\r\n</p></p><ul><li>การแข่งขันฟุตซอลมีการจัดการแข่งขันอยู่เป็นประจำในประเทศแถบอเมริกาใต้\r\nฟุตซอลจึงเป็นเกมการแข่งขันที่ชาวอเมริกาใต้นิยมเล่นกันมาก\r\nโดยเฉพาะอย่างยิ่งในประเทศบราซิล ซึ่งมีนักกีฬาที่มีทักษะความสามารถเฉพาะตัวในการเล่นฟุตบอลสูง\r\nด้วยลีลาอันเร้าใจ และสนุกสนานจากนักเตะชื่อก้องโลกอย่าง เปเล่ ซิโก้ โซคราเตส\r\nเบเบโต้ โรนัลดินโญ่และนักเตะชื่อดังจากบราซิลคนอื่น ๆ\r\nจำนวนมากต่างก็เคยเข้าแข่งขันฟุตซอลมาแล้วทั้งสิ้น\r\nในขณะที่บราซิลมีการเล่นอย่างต่อเนื่องและเป็นการเผยแพร่การเล่นฟุตซอลแก่ประเทศอื่น\r\nๆ ในโลก ภายใต้การดูแลของสหพันธ์ฟุตบอลนานาชาติ (FIFA) <span>และทีมอื่น ๆ ที่มีความสามารถอันยอดเยี่ยมจากทุกมุมโลก<span>&nbsp; การแข่งขันครั้งแรกมีขึ้นในปี ค.ศ. 1965\r\nจัดการแข่งขันครั้งแรกในอเมริกาใต้ ประเทศปารากวัยได้แชมป์ทวีปอเมริกาใต้และในปี\r\nค.ศ. 1979\r\nทีมชาติบราซิลมีชัยชนะเหนือทีมทั้งหมดที่เข้าร่วมการแข่งขันซึ่งทำให้บราซิลเริ่มโดดเด่นเหนือทีมจากกลุ่ม\r\n</span>Pan American ในปี ค.ศ. 1980 และชนะติดต่ออีกครั้งในปี ค.ศ.\r\n1984 อย่างไรก็ตามสหพันธ์ฟุตซอลแห่งสหรัฐอเมริกา (The U.S. Futsal\r\nFederation) ถูกก่อตั้งในปี ค.ศ. 1981 นายออสวัลโด การ์เซีย (Osvaldo\r\nGarcia) เป็นประธานคนแรกของสหพันธ์</span></li></ul><p><p>\r\n\r\n\r\n\r\n</p></p><ul><li>การแข่งขันชิงแชมป์โลกครั้งแรกที่ถูกจัดอย่างไม่เป็นทางการที่กรุงเซาเปาโล\r\nประเทศบราซิล ในปี 1982 และเจ้าภาพบราซิลได้รับชัยชนะ\r\nจากนั้นบราซิลมาได้เป็นแชมป์อีกครั้งในปี ค.ศ. 1985 ที่ประเทศสเปน\r\nแต่พลาดเป็นแชมป์ในการแข่งขันชิงแชมป์โลกครั้งที่ 3\r\nอย่างไรก็ตามบราซิลกลับมากวาดแชมป์โลกอีก 2 ครั้งในปี ค.ศ. 1989 ที่ประเทศฮอลแลนด์\r\nซึ่งสหพันธ์ฟุตบอลนานาชาติ (FIFA) <span>ได้เข้ามาดูแลการแข่งขันชิงแชมป์โลกอย่างเป็นทางการครั้งแรก\r\nซึ่งจัดการแข่งขัน ณ ประเทศฮอลแลนด์ และประเทศบราซิลยังครองความเป็นแชมป์\r\nรองแชมป์ได้แก่ประเทศสเปน อันดับสามประเทศรัสเซีย และในปี ค.ศ. 1992 ที่ฮ่องกง\r\nในขณะที่ทีมฟุตซอล จากประเทศสหรัฐอเมริกาได้ตำแหน่งที่ 3 ในปี ค.ศ. 1989 และที่ 2\r\nในปี ค.ศ. 1992 ในการแข่งขันชิงแชมป์ฟุตซอลโลกนั่นคือตำแหน่งที่สูงที่สุดของทีมจากประเทศสหรัฐอเมริกาในการแข่งขันที่จัดขึ้นโดยสหพันธ์ฟุตบอลนานาชาติ\r\n(FIFA) แม้ว่าทีมจากทวีปอเมริกาใต้จะมีความยิ่งใหญ่ในช่วงที่ผ่านมา\r\nแต่ในปี ค.ศ. 2000 การแข่งขันชิงแชมป์โลก ครั้งที่ 4 ณ ประเทศกัวเตมาลา\r\nทีมจากประเทศสเปนสามารถล้มแชมป์เก่าสามสมัยอันดับหนึ่งของโลกได้อย่างงดงาม\r\nและครองชัยชนะต่อไปอีกในปีค.ศ. 2004 ในการแข่งขันชิงแชมป์โลกครั้งที่ 5 ณ\r\nประเทศไต้หวัน แต่ต่อมาในปี ค.ศ. 2008 ทีมชาติสเปนพ่ายการดวลจุดโทษ 4-3\r\nประตูภายหลังจากเสมอกัน 2 ประตูต่อ 2 ให้กับทีมชาติบราซิล อย่างไรก็ตามการจัดอับดับของสหพันธ์ฟุตบอลนานาชาติ\r\n(FIFA) ทีมชาติสเปนยังคงครองอันดับหนึ่ง\r\nตามด้วยทีมชาติบราซิล และอิตาลีอันดับสาม</span> ในปัจจุบันฟุตซอล ( FUTSAL)\r\n<span>เป็นกีฬาที่ได้รับความนิยมและสนใจจากทุกเพศทุกวัยเนื่องจากเป็นเกมกีฬาที่ตื่นเต้น\r\nสนุกสนานในทุก ๆ นาทีของการแข่งขัน และสามารถเล่นได้ตลอดปี ทุกสภาพอากาศทำให้\r\nฟุตซอล (FUTSAL) กลายเป็นกีฬายอดนิยมสำหรับศตวรรษที่ 21 นี้</span>\r\n\r\n<br></li></ul><p>\r\n\r\n<br></p>', '0000-00-00'),
(17, 'การแข่งขันฟุตบอล 5 คน ในระดับนานาชาติ ', 'พ31101', '<p>\r\n\r\n<p>กีฬาฟุตบอลจัดได้ว่าเป็นเกมกีฬาที่ยิ่งใหญ่และมีผู้ชมคลั่งไคล้กีฬาชนิดนี้มากที่สุดในโลก\r\nเนื่องจากฟุตบอลเป็นเกมที่สนุก ดูง่าย มีสีสันในการเชียร์\r\nโดยเฉพาะในเกมสนามใหญ่ที่เราเรียกว่า เกม 11 คน นั้นเป็นที่นิยมทั้งในระดับสโมสร\r\nในลีกของแต่ละประเทศและระดับนานาชาติ นั่นคือ การแข่งขันฟุตบอลโลก\r\nซึ่งในปัจจุบันนี้กีฬาฟุตบอลไม่แข่งขันเพียงแค่ในสนามใหญ่เท่านั้นยังมีการจัดการแข่งขันฟุตบอลในร่มที่เราเรียกว่า\r\n“\r\nฟุตบอล 5 คน” หรือ “ ฟุตซอล”\r\n(FUTSAL ) นั่นเอง<br></p><p>ค.ศ.1965\r\nจัดการแข่งขันครั้งแรกในอเมริกาใต้ ประเทศปารากวัยได้แชมป์ทวีปอเมริกาใต้</p>\r\n\r\n<p>ค.ศ.1979\r\nมีการแข่งขันชิงแชมป์ทวีปอเมริกาใต้อีก 6 ครั้ง โดยนักเตะแซมบ้า\r\nประเทศบราซิลได้แชมป์ทุกครั้ง</p>\r\n\r\n<p>ค.ศ.1980 และ ค.ศ.1984 การแข่งขัน\r\nแพนอเมริกาคัพ โดยนักเตะแซมบ้า ประเทศบราซิลได้แชมป์ทั้งสองครั้ง</p>\r\n\r\n<p>ค.ศ.1982 การแข่งขันชิงแชมป์โลก\r\n(อย่างไม่เป็นทางการ) ครั้งแรก ณ กรุงเซาเปาโล ประเทศบราซิล และแชมป์ในปีนี้ก็คือ\r\nเจ้าถิ่นประเทศบราซิล นั่นเอง</p>\r\n\r\n<p>ค.ศ.1985 การแข่งขันชิงแชมป์โลก\r\n(อย่างไม่เป็นทางการ) ครั้งที่ 2 ณ ประเทศสเปน ประเทศบราซิลได้แชมป์</p>\r\n\r\n<p>ค.ศ.1988 การแข่งขันชิงแชมป์โลก\r\n(อย่างไม่เป็นทางการ) ครั้งที่ 3 ณ ประเทศออสเตรเลีย\r\nประเทศบราซิลเสียแชมป์ให้แก่ประเทศปารากวัยเป็นครั้งแรก</p>\r\n\r\n<p>ค.ศ.1989 ต่อมาสหพันธ์ฟุตบอลนานาชาติ\r\n(ฟีฟ่า) ได้เข้ามาดูแลการแข่งขันชิงแชมป์โลกเป็นครั้งแรก ซึ่งจัดการแข่งขัน ณ\r\nประเทศฮอลแลนด์ และประเทศบราซิลยังครองเป็นแชมป์ รองแชมป์ประเทศสเปน\r\nอันดับสามประเทศรัสเซีย</p>\r\n\r\n<p>ค.ศ.1992 การแข่งขันชิงแชมป์โลก\r\nครั้งที่ 2 ณ ประเทศฮ่องกง ประเทศบราซิลยังครองความเป็นแชมป์\r\nรองแชมป์ประเทศสหรัฐอเมริกา อันดับสามประเทศสเปน</p>\r\n\r\n<p>ค.ศ.1996 การแข่งขันชิงแชมป์โลก\r\nครั้งที่ 3 ประเทศสเปน ประเทศบราซิลยังครองความเป็นแชมป์ ของโลกอย่างเหนียวแน่น\r\nรองแชมป์ประเทศฮอลแลนด์ อันดับสามประเทศสหรัฐอเมริกา</p>\r\n\r\n<p>ค.ศ.2000 การแข่งขันชิงแชมป์โลก\r\nครั้งที่ 4 ณ ประเทศกัวเตมาลา\r\nประเทศสเปนสามารถล้มแชมป์เก่าสามสมัยอันดับหนึ่งของโลก รองแชมป์ประเทศบราซิล\r\nอันดับสามโปรตุเกส</p>\r\n\r\n<p>ค.ศ.2004\r\nการแข่งขันชิงแชมป์โลกครั้งที่ 5 ณ ประเทศไต้หวัน ประเทศสเปน ยังครองความเป็นแชมป์\r\nรองชนะเลิศประเทศอิตาลี อันดับสามประเทศบราซิล</p>\r\n\r\n\r\n</p>', '0000-00-00'),
(18, 'การแข่งขันฟุตบอล 5 คน ในประเทศไทย ', 'พ31101', '<p>\r\n\r\n<p>ประเทศไทยได้มีการจัดการแข่งขันฟุตบอล\r\n5 คน ขึ้นครั้งแรกในปี พ.ศ.2540 ด้วยความร่วมมือจากหลายๆ\r\nฝ่ายที่ช่วยกันผลักดันกีฬาชนิดนี้ให้ได้รับความนิยมมากขึ้น\r\nไม่ว่าจะเป็นสมาคมฟุตบอลแห่งประเทศไทยในพระบรมราชูปถัมภ์ กรุงเทพมหานคร\r\nการกีฬาแห่งประเทศไทย และเดอะมอลล์ กรุ๊ป จำกัด ร่วมกันจัดการแข่งขันฟุตบอล 5 คน\r\nในรายการ “STAR IN DOOR SOCCER <span>1997” เมื่อวันที่ 12 – 21 กรกฎาคม 2540 ณ เดอะมอลล์\r\nบางกะปิ โดยมี 12 ทีมสโมสรชั้นนำจากไทยแลนด์ลีกเข้าร่วมการแข่งขัน\r\nและทีมการท่าเรือแห่งประเทศไทยชนะเลิศ<span>&nbsp; ในปีต่อมาได้จัดการแข่งขันฟุตบอล\r\n5 คน ขึ้นอีกเป็นครั้งที่ 2 ในครั้งนี้ </span>“ ทีมกรุงเทพมหานคร”\r\nชนะแชมป์เก่าการท่าเรือแห่งประเทศไทย ปี พ.ศ.2543\r\nได้มีการจัดการแข่งขันฟุตซอลขึ้นเป็นครั้งที่ 3\r\nโดยมีการแข่งขันรอบคัดเลือกในแต่ละภาคเพื่อนำทีมชนะเลิศและรองชนะเลิศมาแข่งขันกับทีมสโมสรชั้นนำจากไทยลีก\r\nในการแข่งขันฟุตซอลชิงชนะเลิศแห่งประเทศไทย\r\nซึ่งจากความสำเร็จในการแข่งขันครั้งนี้ทำให้กีฬาฟุตซอลเป็นที่นิยมมากยิ่งขึ้น ต่อมาประเทศไทยได้มีโอกาสเป็นเจ้าภาพจัดการแข่งขันฟุตซอลชิงแชมป์เอเชีย\r\nและจากการแข่งขันดังกล่าวทำให้ประเทศไทยได้อันดับสามและได้สิทธิ์เดินทางไปแข่งขันฟุตซอลชิงแชมป์โลกรอบสุดท้าย\r\nระหว่างวันที่ 18 พฤศจิกายน – 3 ธันวาคม 2543 ณ ประเทศกัวเตมาลา<span>&nbsp; ในปัจจุบันฟุตซอล ( </span>FUTSAL ) เป็นกีฬาที่ได้รับความนิยมและสนใจจากทุกเพศทุกวัย\r\nเนื่องจากเป็นเกมกีฬาที่ตื่นเต้น สนุกสนานในทุกๆ นาทีของการแข่งขัน และสามารถเล่นได้ตลอดปี\r\nทุกสภาพอากาศทำให้ ฟุตซอล (FUTSAL ) กลายเป็นกีฬายอดนิยมรับสหัสวรรษใหม่นี้</span></p>\r\n\r\n<br></p>', '0000-00-00'),
(19, 'กติกาฟุตซอล  ', 'พ31101', '<p>\r\n\r\n</p><ul><li>สนามแข่งขันฟุตซอล</li></ul><p>\r\n\r\n<p>สนามเเข่งขันต้องเป็นรูปสี่เหลี่ยมผืนผ้า\r\nความยาวเส้นข้างต้องยาวกว่าความยาวเส้นประตู โดยสนามมีความยาวต่ำสุด 25 เมตร\r\nสูงสุด 42 เมตร ความกว้างต่ำสุด 15 เมตร สูงสุด 25 เมตร</p><p><br></p>\r\n</p><ul><li>ลูกบอลฟุตซอล</li></ul><p>\r\n\r\n<p>ลูกบอลต้องเป็นทรงกลม\r\nทำด้วยหนังหรือวัสดุอื่น ๆ เส้นรอบวงไม้น้อยกว่า 62 เซนติเมตร แต่ไม่เกิน 64\r\nเซนติเมตร ขณะการแข่งขัน ลูกบอลต้องมีความดันลม ไม่น้อยกว่า 400 กรัม ไม่มากกว่า\r\n440 กรัม ความดันลมของลูกบอลจะอยู่ที่ ความดันลมของลูกบอล 0.4\r\n– <span>0.6 ระดับบรรยากาศ</span></p><p><span><br></span></p>\r\n</p><ul><li>ผู้เล่นฟุตซอล</li></ul><p>\r\n\r\n<p>จะมีผู้เล่นทั้งสองทีม ทีมละไม่เกิน 5\r\nคนอยู่บนสนาม และหนึ่งใน 5 คนนี้ ต้องเป็นผู้รักษาประตู\r\nและอนุญาตให้มีผู้เล่นสำรองไม่เกิน 7 คน อนุญาตให้เปลี่ยนตัวผู้เล่นเมื่อไหร่ก็ได้\r\nไม่จำกัดจำนวน สามารถเปลี่ยนตัวได้ตลอดเวลา\r\nผู้เล่นที่เปลี่ยนออกไปแล้วสามารถกลับเข้าไปเล่นใหม่\r\nแต่ในกรณีที่ทีมหนึ่งที่เหลือผู้เล่นน้อยกว่า 3 คน รวมผู้รักษาประตู\r\nจะต้องยกเลิกการเเข่งขัน<span>&nbsp;\r\nผู้เล่นต้องไม่สวมใส่เครื่องประดับใด ๆ ที่เป็นอันตรายต่อตนเองและผู้อื่น\r\nโดยอุปกรณ์เบื้องต้นมี</span></p>\r\n\r\n<p>1.<span>&nbsp;\r\nเสื้อยืดหรือเสื้อเชิ้ต</span></p>\r\n\r\n<p>2.<span>&nbsp;\r\nกางเกงขาสั้น (ถ้าสวมกางเกงปรับอุณหภูมิ\r\nสีของกางเกงนั้นจะต้องเป็นสีเดียวกันกับสีหลักของกางเกง</span></p>\r\n\r\n<p>3.<span>&nbsp;\r\nถุงเท้ายาว</span></p>\r\n\r\n<p>4.<span>&nbsp;\r\nสนับแข้ง</span></p>\r\n\r\n<p>5.<span>&nbsp;\r\nรองเท้าผ้าใบหรือรองเท้าหนังนิ่ม หรือรองเท้าออกกำลังกายพื้นยาง</span></p><p><span><br></span></p>\r\n</p><ul><li>ระยะเวลาการแข่งขัน</li></ul><p>\r\n\r\n<p>การแข่งขันทีทั้งหมด 2 ครึ่ง ครึ่งละ\r\n20 นาที และระยะเวลาการแข่งขันแต่ละครึ่งอาจเพิ่มเวลาเตะลูกโทษ ณ จุดโทษ\r\nส่วนการขอเวลานอกนั้น ทั้งสองทีมมีสิทธิขอเวลานอก 1 นาที ในแต่ละครึ่งเวลา</p><p><br></p>\r\n</p><ul><li>การนับประตู</li></ul><p>\r\n\r\n<p>การนับประตูจะเป็นผลเมื่อบอลทั้งลูกผ่านเส้นประตูระหว่างเสาประตูใต้คานประตู\r\nแต่ต้องไม่มีการทำผิดกติกาโดยทีมที่ทำประตู\r\nผู้รักษาประตูไม่สามารถทำประตูได้ด้วยมือ\r\nและผู้เล่นฝ่ายรุกไม่สามารถทำประตูได้ด้วยแขน</p>\r\n\r\n<br></p>', '0000-00-00'),
(20, 'ประโยชน์ของการเล่นฟุตซอล', 'พ31101', '<p>\r\n\r\n<p><b>ประโยชน์ของการเล่นฟุตซอล</b><br></p><p>1.\r\nการเล่นกีฬาฟุตซอลจะต้องเคลื่อนไหวตลอดเวลา มีความเชื่อมั่นในตัวเอง มีไหวพริบดี\r\nสามารถแก้ไขปัญหาต่างๆได้ ตัดสินใจรวดเร็วแน่นอน แม่นยำ\r\nดังนั้นฟุตซอลจึงเป็นกีฬาประเภทหนึ่งที่ช่วยฝึกฝนให้ผู้เล่นมีไหวพริบและแก้ปัญหาอย่างฉับพลันได้</p>\r\n\r\n<p>2.\r\nฟุตซอลเป็นกีฬาอีกประเภทหนึ่งที่ช่วยเสริมสร้างสมรรถภาพทางร่างกายให้แข็งแรง\r\nช่วยให้ระบบต่างๆในร่างกายทำงานอย่างมีประสิทธิภาพ เช่น ระบบกล้ามเนื้อ\r\nระบบการหายใจ ระบบขับถ่าย ระบบการไหลเวียนของโลหิต เป็นต้น</p>\r\n\r\n<p>3.\r\nฟุตซอลเป็นกีฬาที่ช่วยส่งเสิรมกิจกรรมที่รวมการเคลื่อนไหว เช่น การวิ่งหลบหลีก\r\nการแย่ง การรับ การส่ง การกระโดด การเตะ ตลอดจนการใช้เท้าให้สัมพันธ์กับสายตาด้วย</p>\r\n\r\n<br></p>', '0000-00-00'),
(21, 'วิวัฒนาการของฟุตบอล', 'พ33101', '<p>\r\n\r\n<p>ประวัติฟุตบอล –\r\n<span>ฟุตบอล (Football) หรือซอคเก้อร์ (Soccer)\r\nเป็นกีฬาที่มีผู้สนใจที่จะชมการแข่งขันและเข้าร่วมเล่นมากที่สุดในโลก\r\nชนชาติใดเป็นผู้กำเนิดกีฬาชนิดนี้อย่างแท้จริงนั้นไม่อาจจะยืนยันได้แน่นอน\r\nเพราะแต่ละชนชาติต่างยืนยันว่าเกิดจากประเทศของตน\r\nแต่ในประเทศฝรั่งเศสและประเทศอิตาลี ได้มีการละเล่นชนิดหนึ่งที่เรียกว่า “ซูเลอ” (Soule) หรือจิโอโค เดล คาซิโอ (Gioco\r\nDel Calcio) มีลักษณะการเล่นที่คล้ายคลึงกับกีฬาฟุตบอลในปัจจุบัน\r\nทั้งสองประเทศอาจจะถกเถียงกันว่ากีฬาฟุตบอลถือกำเนิดจากประเทศของตน\r\nอันเป็นการหาข้อยุติไม่ได้ เพราะขาดหลักฐานยืนยันอย่างแท้จริง ดังนั้น\r\nประวัติของกีฬาฟุตบอลที่มีหลักฐานที่แท้จริงสามารถจะอ้างอิงได้\r\nเพราะการเล่นที่มีกติกาการแข่งขันที่แน่นอน\r\nคือประเทศอังกฤษเพราะประเทศอังกฤษตั้งสมาคมฟุตบอลในปี พ.ศ. 2406 และฟุตบอลอาชีพของอังกฤษเกิดขึ้นในปี พ.ศ. 2431</span></p>\r\n\r\n<p>\r\n\r\n</p><p></p>\r\n\r\n<p>วิวัฒนาการด้านฟุตบอลจะเป็นไปพร้อมกับความเจริญก้าวหน้าของมนุษย์ตลอดมา\r\nต้นกำเนิดกีฬาตะวันออกไกลจะได้รับอิทธิพลมาจากสงครามครั้งสำคัญๆ เช่น\r\nสงครามพระเจ้าอเล็กซานเดอร์มหาราช ได้นำเอา “<span>แกลโล-โรมัน”\r\n(Gello-Roman) พร้อมกีฬาต่างๆ เข้ามาสู่เมืองกอล (Gaul) อันเป็นรากฐานส่วนหนึ่งของกีฬาฟุตบอลในอนาคต และการเล่นฮาร์ปาสตัม (Harpastum)\r\nได้ถูกดัดแปลงมาเป็นกีฬาซูเลอ</span></p>\r\n\r\n<br></p>', '0000-00-00'),
(22, 'วิวัฒนาการของฟุตบอลในแต่ละทวีป', 'พ33101', '<p>\r\n\r\n<p><b>ภาคตะวันออกไกล</b></p>\r\n\r\n<p>ขงจื้อได้กล่าวไว้ในหนังสือ “<span>กังฟู” เกี่ยวกับกีฬา\r\nโดยเฉพาะอย่างยิ่งกีฬาที่ใช้เท้าและศีรษะในสมัยจักรพรรดิ์ เซิงติ (Emperor\r\nCneng Ti) (ปี 32 ก่อนคริสตกาล)\r\nมีการเล่นกีฬาที่คล้ายกับฟุตบอลซึ่งเรียกว่า”ซือ-ซู”\r\n(Tsu-Chu) ซึ่งหมายถึงการเตะลูกหนังด้วยเท้า\r\nกีฬาชนิดนี้ได้รับความนิยมอย่างกว้างขวาง\r\nซึ่งนักประพันธ์และนักประวัติศาสตร์ในสมัยนั้นได้ยกย่องผู้เล่นที่มีชื่อเสียงให้เป็นวีรบุรุษของชาติ\r\nและในสมัยเดียวกันได้มีการเล่นคล้ายฟุตบอลในประเทศญี่ปุ่นอีกด้วย</span></p>\r\n\r\n<p><b>ภาคตะวันออกกลาง</b></p>\r\n\r\n<p>ในกรุงโรม\r\nความเจริญของตะวันออกไกลได้แผ่ขยายถึงตะวันออกกลางโดยเฉพาะอย่างยิ่งจากอิทธิพลของสงคราม\r\nโดยพระเจ้าอเล็กซานเดอร์มหาราช การเล่นกีฬาชนิดหนึ่งเรียกว่า ฮาร์ปาสตัม\r\nเป็นกีฬาที่นิยมของชาวโรมันและชาวกรีกโบราณวิธีการเล่นคือ มีประตูคนละข้าง\r\nแล้วเตะลูกบอลไปยังจุดหมายที่ต้องการ เช่น จากหมู่บ้านหนึ่งไปอีกหมู่บ้านหนึ่ง\r\nการเล่นจะเป็นการเตะ หรือการขว้างไปข้างหน้าฮาร์ปาสตัม\r\nหมายถึงการเหวี่ยงไปข้างหน้า\r\nการเล่นกีฬาฮาร์ปาสตัมในกรุงโรมดูเหมือนจะเป็นต้นกำเนิดของกีฬาซึ่งมีการเล่นในสมัยกลางในการเล่นฮาร์ปาสตัม\r\nขนาดของสนามจะเล็กกว่าสนามกีฬาซูเลอ แต่จุดประสงค์ของกีฬาทั้งสองคือ การนำลูกบอล\r\nไปยังแดนของตน แต่เนื่องจากมีเสียงอึกทึกโครมครามจากการวิ่งแย่งลูกบอล\r\nซึ่งอาจจะทำให้เกิดอุบัติเหตุได้มากมาย อันเป็นข้อห้ามของพระเจ้า\r\nจึงมีพระบรมราชโองการในนามของพระเจ้าแผ่นดินห้ามเล่นกีฬาดังกล่าวในเมือง\r\nผู้ฝ่าฝืนมีโทษถึงจำคุก นอกจากนี้ยังมีข้อห้ามซึ่งออกในวันที่ 12<span> มิถุนายน พ.ศ.1892 ขอให้เล่นยิงธนูในวันฉลองต่าง ๆ\r\nแทนการเล่นเกมฟุตบอล ในโอกาสต่อมากีฬาฟุตบอลได้จัดให้มีการแข่งขันกันอีกครั้ง\r\nซึ่งเป็นการเผชิญหน้ากันระหว่างทีมต่างๆ ที่อยู่ห่างกันประมาณ 3-4 ไมล์ ( 5-6.5 กิโลเมตร) </span></p>\r\n\r\n<p>ในปี พ.ศ. 2344<span> กีฬาชนิดนี้ได้ขัดเกลาให้ดีขึ้น\r\nมีการกำหนดจำนวนผู้เล่นให้เท่ากันในแต่ละข้าง ขนาดของสนามอยู่ในระหว่าง 80\r\n– 100 หลา (73-91 เมตร)\r\nและมีประตูทั้งสองข้างที่ริมสุดของสนามซึ่งทำด้วยไม้ 2 อัน\r\nห่างกัน 2-3 ฟุต</span></p>\r\n\r\n<p>ในปี พ.ศ. 2366<span> ได้จัดให้มีการเล่นฟุตบอลในรูปแบบของการเล่นใน ปัจจุบัน William\r\nAlice คือผู้เริ่มวางกฎบังคับต่างๆ สำหรับกีฬาฟุตบอลและรักบี้ ในปี\r\nพ.ศ. 2393 ได้มีการออกระเบียบและกฎของการเล่นไปสู่\r\nดินแดนต่างๆ ให้ปฏิบัติตาม โดยจำกัดจำนวนผู้เล่นให้มีข้างละ 15-20 คน</span></p>\r\n\r\n<p></p>\r\n\r\n<p>ในปี พ.ศ. 2413<span> มีการกำหนดผู้เล่นให้เหลือข้างละ 11 คน\r\nโดยมีผู้เล่นกองหน้า 9 คน และผู้เล่นรักษาประตู 2 คน โดยผู้รักษาประตูใช้เท้าเล่นเหมือน 9 คนแรกจนกระทั่งให้เหลือผู้รักษาประตู\r\n1 คน แต่อนุญาตให้ใช้มือจับลูกบอลได้ในปี พ.ศ. 2423</span></p>\r\n\r\n<p><b>ในปี พ.ศ. </b><b>2400<span> สโมสรฟุตบอลได้ก่อตั้งเป็นครั้งแรกที่เมืองเซนพัสด์ประเทศอังกฤษ\r\nและต่อมาในวันที่ 26</span></b>ตุลาคม\r\nพ.ศ. 2406<span> สโมสรฟุตบอล 11\r\nแห่งได้มารวมกันที่กรุงลอนดอนเพื่อก่อตั้งสมาคมฟุตบอลขึ้น\r\nซึ่งถือเป็นรากฐานในการกำเนิดสมาคมแห่งชาติ จนถึง 140 สมาคม\r\nและทำให้ผู้เล่นฟุตบอลต้องเล่นตามกฎและกติกาของสมาคมฟุตบอล จนเวลาผ่านไปจากคำว่า Association\r\nก็ย่อเป็น Assoc และกลายเป็น Soccer ขึ้นในที่สุด ซึ่งนิยมเรียกกันในประเทศอังกฤษ แต่ชาวอเมริกันเรียกว่า Football\r\nหมายถึง American football</span></p>\r\n\r\n<p></p>\r\n\r\n<p>ภายนอกเกาะอังกฤษ พวกกะลาสีเรือ ทหาร\r\nพ่อค้า วิศวกร หรือแม้แต่นักบวชได้นำกีฬาชนิดนี้ไปเผยแพร่\r\nประเทศเดนมาร์กเป็นประเทศที่ 2<span> ในยุโรป</span></p>\r\n\r\n<p>ในอเมริกาใต้\r\nสโมสรแรกได้ถูกตั้งขึ้นในประเทศอาร์เจนตินา เมื่อพี่น้องชาวอังกฤษ 2<span> คน ได้ลงข้อความโฆษณาในหนังสือพิมพ์ของเมืองบูเอโนสไอเรส (Buenos\r\nAires) เพื่อ หาผู้อาสาสมัคร ในปี พ.ศ. 2427\r\nกีฬาฟุตบอลก็กลายมาเป็นวิชาหนึ่งในโรงเรียนของเมืองบูเอโนสไอเรส\r\nการแข่งขันระดับชาติครั้งแรกในทวีปอเมริกาใต้ คือ\r\nการแข่งขันระหว่างอาร์เจนตินากับอุรุกวัย ในปี พ.ศ.2448\r\nแต่อเมริกาเหนือเริ่มแข่งขันเมื่อปี พ.ศ. 2435</span></p>\r\n\r\n<p>ในอิตาลี\r\nฮาร์ปาสตัมเป็นต้นกำเนิดจิโอโค เดล คาลซิโอ ผู้เล่นกีฬาจะเป็นผู้นำทางสังคม\r\nหรือแม้แต่ผู้นำชั้นสูงของศาสนา เช่นสันตปาปา เกลาเมนต์ที่ 7<span> ลีออนที่ 10 และเออร์เบนที่ 7\r\nเป็นถึงแชมเปี้ยนในกีฬาฟลอเรนไทน์ฟุตบอล\r\nต่อมาชาวโรมันได้ดัดแปลงเกมการเล่นฮาร์ปาสตัมเสียใหม่\r\nโดยกำหนดให้ใช้เท้าแตะลูกบอลเท่านั้น ส่วนมือให้ใช้เฉพาะการทุ่มลูกบอล\r\nซึ่งนักรบชาวโรมัน นิยมเล่นกันมาก</span></p>\r\n\r\n<p>กีฬาฮาร์ปาสตัมซึ่งมีต้นกำเนิดจากสมัยโรมันได้ถูกแปลงมาเป็นกีฬาซูลอหรือซูเลอ\r\nกีฬาชนิดนี้เหมือนกับฮาร์ปาสตัม คือ นำลูกบอลกลับไปยังแดนของตน\r\nแต่สนามมีขนาดกว้างกว่ามาก</p>\r\n\r\n<p>การเล่นซูเลอมักจะมีขึ้นในบ่ายวันอาทิตย์หลังการสวดมนต์เย็น\r\nจะมีการแข่งขันสำคัญในช่วงเวลาดีคาร์นิวาล\r\nกีฬาชนิดนี้เป็นที่นิยมมากในเขตปริตานีและมอร์ลังดี กีฬานี้ได้ถูกเผยแพร่ไปยังอังกฤษโดยผู้ติดตามของวิลเลี่ยมผู้พิชิตภายหลัง\r\nการรบที่เฮสติ้ง (Hasting)</p>\r\n\r\n<p></p>\r\n\r\n<p>เมื่อ 900<span> ปีกว่ามาแล้ว ประเทศอังกฤษได้ตกอยู่ในความปกครองของพวกเคนส์\r\nเชื้อสายโรมัน ซึ่งยกกองทัพมาตีหมู่เกาะอังกฤษตอนใต้ และได้ปกครองเรื่อยมาจนถึงปี\r\nพ.ศ. 1589 อังกฤษเริ่มเข้มแข็งขึ้น\r\nและสามารถขับไล่พวกเคนส์ออกจากประเทศได้ หลังจากนั้น 2-3 ปี\r\nอังกฤษจึงเริ่มปรับปรุงประเทศเป็นการใหญ่ มีการขุดอุโมงค์ตามพื้นที่หลายแห่ง\r\nซึ่งในการขุดอุโมงค์คนงานคนหนึ่งได้ขุดไปพบกะโหลกศีรษะในบริเวณที่เคยเป็นสนามรบ\r\nและเป็นที่ฝังศพของพวกเคนส์มาก่อนทุกคนในที่นั้นแน่ใจว่าเป็นกะโหลกศีรษะของพวกเคนส์\r\nอารมณ์แค้นจึงเกิดขึ้นทันทีเมื่อต่างคนต่างคิดถึงเหตุการณ์ที่ถูกพวกเคนส์กดขี่ทารุณจิตใจคนอังกฤษในสมัยนั้นด้วยเหตุผลนี้\r\nคนงานคนหนึ่งจึงเตะกะโหลกศีรษะนั้นทันที ส่วนคนอื่นๆ ที่อยู่ในบริเวณนั้นก็พากันหยุดงานชั่วคราว\r\nแล้วหันมาเตะกะโหลกศีรษะเป็นการใหญ่ เพื่อระบายอารมณ์แค้นที่เก็บไว้อย่างสนุกสนาน\r\nผลที่สุดเมื่อพวกนี้หากะโหลกศีรษะเตะกันไม่ได้ก็เอาถุงลมของวัวมาทำเป็นลูกกลมขึ้นเตะแทนกะโหลกศีรษะ\r\nปรากฏว่าเป็นที่รื่นเริงสนุกสนามกันมาก ต่อมาชาวโรมันได้นำเกมนี้ไปเล่นในอังกฤษ\r\nจากนั้นชาวอังกฤษก็ได้ปรับปรุงวิธีการเล่น เทคนิคการเล่น\r\nตลอดจนกติกาให้เหมือนในสมัยปัจจุบัน คือเกมฟุตบอลที่ใช้เท้าเล่น\r\nแต่ในระยะแรกของการเล่นฟุตบอลจะเล่นกันเป็นกลุ่มๆ เฉพาะพวกคนธรรมดาเท่านั้น\r\nไม่มีการจำกัดจำนวนผู้เล่น ประตูจะห่างกันเป็นไมล์ และใช้เวลาในการเล่นหลายชั่วโมง\r\nจะเป็นการเล่นระหว่างทหารใหม่ที่ถูกเกณฑ์ นักบวช คนที่แต่งงานแล้ว คนโสด\r\nและพวกพ่อค้า เกมชนิดได้กลายเป็นสิ่งฉลองในงานพิธีต่างๆ เช่น ในวันโชรพ ทิวส์เดย์\r\n(Shrove Tuesday) จะมีฟุตบอลนัดสำคัญให้คนได้ชม\r\nเกมในสมัยนั้นจะเล่นกันอย่างรุนแรงและมีการบาดเจ็บกันมาก</span></p>\r\n\r\n<p>ในวันที่ 13<span> เมษายน พ.ศ. 1857 พระเจ้าเอ๊ดเวิร์ดที่ 2 ได้ทรงออกพระราชกฤษฎีกา\r\nเนื่องจากมีเสียงอึกทึกครึกโครมจาการวิ่งแย่งลูกบอล\r\nซึ่งอาจจะทำให้เกิดอุบัติเหตุมากมาย อันเป็นข้อห้ามของพระเจ้า\r\nโดยห้ามเล่นกีฬาดังกล่าว ผู้ใดฝ่าฝืนมีโทษจำคุก</span></p>\r\n\r\n<p>ฟุตบอลได้เริ่มแข่งขันภายใต้กฎของสมาคมแห่งชาติ\r\nเมื่อปี พ.ศ. 2412<span>\r\nระหว่างทีมรัตเกอร์กับทีมบรินท์ตัน จากนั้นกิจการฟุตบอลได้เจริญขึ้นช้าๆ\r\nในต่างจังหวัดจนกระทั่งสงครามโลกครั้งที่ 1\r\nได้มีการตั้งสมาคมฟุตบอลต่างจังหวัดขึ้นในปี พ.ศ. 2450\r\nและมีการฝึกสอนในปี พ.ศ. 2484</span></p>\r\n\r\n<p></p>\r\n\r\n<p><b>ในทวีปเอเชีย</b></p>\r\n\r\n<p>อินเดียเป็นประเทศแรกที่เริ่มเล่นฟุตบอล\r\nศาสตราจารย์จากวิทยาลัยกัลกัตตา เป็นผู้นำสำเนากฎหมายการเล่นมาเผยแพร่ในปี พ.ศ. 2426<span> และในปี พ.ศ. 2435\r\nได้มีการแข่งขันชิงถ้วยรางวัลเป็นครั้งแรก\r\nในทวีปซึ่งยังไม่มีชื่อเสียงในด้านการเล่นฟุตบอล กีฬาชนิดนี้ก็ได้เริ่มมีการเล่นมาก่อนร่วมร้อยปีแล้ว\r\nเช่น สมาคมฟุตบอลแห่งนิวเซาท์เวลส์ ได้ถูกตั้งขึ้นในออสเตรเลีย ปี พ.ศ. 2425 และสมาคมฟุตบอลของนิวซีแลนด์ได้ถูกตั้งขึ้นหลังจากนั้น 9 ปี</span></p>\r\n<p><b>ในแอฟริกา</b></p>\r\n\r\n<p>สมาคมระดับชาติแห่งแรกได้ถูกตั้งขึ้นในประเทศแอฟริกาใต้\r\nแต่อียิปต์เป็นประเทศแรกที่มีการแข่งขันระดับชาติในปี พ.ศ. 2467<span> คือ 3 ปี หลังจากที่ได้ก่อตั้งสมาคมขึ้น\r\nและอียิปต์สามารถเอาชนะฮังการีได้ 3-0\r\nในกีฬาโอลิมปิกที่ปารีส</span></p>\r\n\r\n<p>การแข่งขันระดับชาติเป็นการแข่งขันระหว่างอังกฤษกับสกอตแลนด์\r\nตั้งแต่ปี พ.ศ. 2415<span>\r\nและในปีแรกของศตวรรษที่ 20 โดยประเทศยุโรปอื่นๆ\r\nอย่างไรก็ตามในปี พ.ศ. 2447 กลุ่มประเทศต่างๆ\r\nในแถบนี้ได้ประชุมกันที่ปารีสเพื่อตั้งสมาคมฟุตบอลนานาชาติขึ้น\r\nในครั้งแรกก่อนการจัดตั้งสหพันธ์ 20 วัน\r\nสเปนและเดนมาร์กไม่เคยร่วมการแข่งขันระดับชาติมาก่อน และ 3\r\nประเทศใน 7\r\nประเทศที่เข้าร่วมประชุมยังไม่มีสมาคมฟุตบอลในชาติของตน\r\nแต่ฟีฟ่าก็แข็งแกร่งขึ้นเรื่อยมา โดยมีสมาชิก 5 ชาติ ในปี\r\nพ.ศ. 2481 และ 73 ชาติ ในปี พ.ศ. 2493 และในปัจจุบันมีสมาชิกถึง 146 ประเทศ\r\nที่มีส่วนร่วมในกิจกรรมของฟีฟ่า ทำให้ฟีฟ่าเป็นองค์การกีฬาที่ใหญ่ที่สุดในโลก</span></p>\r\n\r\n<p>สมาพันธ์ประจำทวีปของสมาคมฟุตบอลแห่งแรกที่ตั้งขึ้นคือ\r\nConmebol\r\n<span>ซึ่งเป็นสมาพันธ์ของอเมริกาใต้\r\nสมาพันธ์นี้ได้ถูกจัดขึ้นเพื่อจัดตั้งเพื่อจัดการแข่งขันชิงชนะเลิศภายในทวีปอเมริกาใต้\r\nในปี พ.ศ. 2460 เกือบครึ่งศตวรรษ ต่อมาเมื่การแข่งขันภายในทวีปได้แพร่หลายมากขึ้น\r\nจึงได้มีการจัดตั้งสมาพันธ์ในทวีปอื่นๆ ขึ้นอีกคือสหภาพสมาคมฟุตบอลยุโรป ในปี พ.ศ.\r\n2497 ซึ่งเป็นปีเดียวกับการจัดตั้งในทวีปเอเชีย และ 2 ปี ก่อนการจัดตั้งสมาคมฟุตบอลยุโรป ในปี พ.ศ. 2497\r\nซึ่งเป็นปีเดียวกับการจัดตั้งสหพันธ์ฟุตบอลแห่งแอฟริกา (Concacaf)หรือสหพันธ์ฟุตบอลแห่งอเมริกากลาง อเมริกาเหนือ และแคริบเบี้ยน\r\nได้ถูกจัดตั้งขึ้นในปี พ.ศ. 2504\r\nและน้องใหม่ในวงการฟุตบอลโลกคือ สมาพันธ์ฟุตบอลแห่งโอเชียนเนีย (Oceannir)</span></p>\r\n\r\n<br></p>', '0000-00-00'),
(23, 'สหพันธ์ฟุตบอลนานาชาติ', 'พ33101', '<p>\r\n\r\n<p>สหพันธ์ฟุตบอลนานาชาติ (Federation\r\nInternational Football Association FIFA) <span>ก่อตั้งขึ้นที่กรุงปารีส\r\nประเทศฝรั่งเศส เมื่อ พ.ศ. 2447\r\nโดยสมาคมฟุตบอลแห่งประเทศฝรั่งเศส และประเทศที่เข้าร่วมก่อตั้ง 7 ประเทศคือ ฝรั่งเศส เบลเยียม เดนมาร์ก เนเธอร์แลนด์ สเปน สวีเดน\r\nและสวิตเซอร์แลนด์ มีสำนักงานใหญ่ตั้งอยู่ที่ เมืองซูริก ประเทศสวิตเซอร์แลนด์</span></p>\r\n<p><b>สมาพันธ์ฟุตบอลที่ได้รับการรับรองจากสหพันธ์ฟุตบอลนานาชาติ</b></p>\r\n\r\n<p></p>\r\n\r\n<p>1.) Africa (C.A.F.) <span>เป็นเขตที่มีสมาชิกมากที่สุด\r\nได้แก่ ประเทศแอลจีเรีย ตูนิเซีย แซร์ ไนจีเรีย และซูดาน เป็นต้น</span></p>\r\n\r\n<p>2.) America-North and Central Caribbean\r\n(Concacaf) <span>ได้แก่ ประเทศสหรัฐอเมริกา แคนาดา เม็กซิโก คิวบา เอติ\r\nเอลซัลวาดอร์ กัวเตมาลา และฮอนดูรัส เป็นต้น</span></p>\r\n\r\n<p>3.) South America (Conmebol) <span>ได้แก่\r\nประเทศเปรู บราซิล อุรุกวัย โบลิเวีย อาร์เจนตินา ชิลี เวเนซุเอลา อีคิวเตอร์\r\nและโคลัมเบีย เป็นต้น</span></p>\r\n\r\n<p>4.) Asia (A.F.C.) <span>เป็นเขตที่มีสมาชิกรองจากแอฟริกา\r\nได้แก่ ประเทศไทย มาเลเซีย เกาหลี ญี่ปุ่น ฮ่องกง เลบานอน อิสราเอล อิหร่าน\r\nจอร์แดน และเนปาล เป็นต้น</span></p>\r\n\r\n<p>5.) Europe (U.E.F.A.) <span>เป็นเขตที่มีการพัฒนามากที่สุด\r\nได้แก่ ประเทศอังกฤษ ฝรั่งเศส เยอรมัน ฮังการี อิตาลี สกอตแลนด์ รัสเซีย สวีเดน\r\nสเปน และเนเธอร์แลนด์ เป็นต้น</span></p>\r\n\r\n<p>6.) Oceania <span>เป็นเขตที่มีสมาชิกน้อยที่สุดและเพิ่งจะได้รับการแบ่งแยก\r\nได้แก่ ประเทศออสเตรเลีย นิวซีแลนด์ ฟิจิ และปาปัวนิวกินี เป็นต้น ซึ่งประเทศต่างๆ\r\nที่เป็นสมาชิกต้องเสียค่าบำรุงเป็นรายปี ปีละ 300 ฟรังสวิสส์\r\nหรือประมาณ 2,400 บาท</span></p>\r\n\r\n<br></p>', '0000-00-00'),
(24, 'สหพันธ์ฟุตบอลแห่งเอเชีย', 'พ33101', '<p>\r\n\r\n<p>ในทวีปเอเชียมีการจัดตั้งสหพันธ์ฟุตบอลแห่งเอเอเชีย\r\n(A.F.C.)\r\n<span>เพื่อดำเนินการด้านฟุตบอล ดังนี้</span></p>\r\n\r\n<p>พ.ศ. 2495<span> มีการแข่งขันกีฬาโอลิมปิกที่เฮลซิงกิ ประเทศฟินแลนด์\r\nโดยมีนักกีฬาและเจ้าหน้าที่จากประเทศในเอเชียเข้ามาร่วมการแข่งขันด้วย\r\nจึงได้ปรึกษาหารือกันในการจัดตั้งสหพันธ์ฟุตบอลเอเชียขึ้น</span></p>\r\n\r\n<p>พ.ศ. 2497<span> มีการแข่งขันเอเชียนเกมส์ที่กรุงมะนิลา ประเทศฟิลิปปินส์ ก็ได้เริ่มตั้งคณะกรรมการจากชาติต่างๆ\r\nที่เข้าร่วมเป็นสมาชิก 12 ประเทศ</span></p>\r\n\r\n<p>พ.ศ. 2501<span> มีการแข่งขันกีฬาเอเชียนเกมส์ที่ประเทศญี่ปุ่น\r\nได้มีการประชุมเกี่ยวกับเรื่องนี้อีก และมีประเทศเข้าร่วมเป็นสมาชิกรวมเป็น 35 ประเทศ</span></p>\r\n\r\n<p>พ.ศ. 2509<span> ฟีฟ่าได้มองเห็นความสำคัญของ A.F.C. จึงได้กำหนดให้มีเลขานุการประจำในเอเชีย\r\nโดยออกค่าใช้จ่ายให้ทั้งหมด รวมทั้งเงินเดือน และคนแรกที่ได้รับตำแหน่งคือ Khow\r\nEve Turk</span></p>\r\n\r\n<p>พ.ศ. 2517<span> ในการแข่งขันกีฬาเอเชียนเกมส์ที่เตหะราน\r\nประเทศอิหร่านได้มีการประชุมประเทศสมาชิก A.F.C. และที่ประชุมได้ลงมติขับไล่อิสราเอล\r\nออกจากสมาชิก และให้จีนแดงเข้าเป็นสมาชิกแทน ทั้งๆ\r\nที่จีนแดงไม่ได้เป็นสมาชิกของฟีฟ่า\r\nนับว่าเป็นการสร้างเหตุการณ์ที่ประหลาดใจให้กับบุคคลทั่วไปเป็นอย่างมากทั้งนี้เนื่องจากเหตุผลทางการเมือง</span></p>\r\n\r\n<p>พ.ศ. 2519<span> มีการประชุมกันที่ประเทศมาเลเซีย\r\nปรากฏว่าประเทศสมาชิกได้ลงมติให้ขับไล่ประเทศไต้หวันออกจากสมาชิก\r\nและให้รับจีนแดงเข้ามาเป็นสมาชิกแทน ทั้งๆ\r\nที่ไต้หวันเป็นประเทศที่ร่วมกันก่อตั้งสหพันธ์ขึ้นมา</span></p>\r\n\r\n<p></p>\r\n\r\n<p><b>งานของสหพันธ์ฟุตบอลแห่งเอเชีย</b></p>\r\n\r\n<p>– <span>ดำเนินการจัดการแข่งขันและควบคุม Asian\r\nCup</span></p>\r\n\r\n<p>– <span>ดำเนินการจัดการแข่งขันและควบคุม Asian\r\nYouth</span></p>\r\n\r\n<p>– <span>ดำเนินการจัดการแข่งขันและควบคุมฟุตบอลโลกรอบคัดเลือก</span></p>\r\n\r\n<p>– <span>ดำเนินการจัดการแข่งขันและควบคุม Pre-Olympic</span></p>\r\n\r\n<p>– <span>ดำเนินการจัดการแข่งขันและควบคุม World\r\nYouth</span></p>\r\n\r\n<p>– <span>ควบคุมการแข่งขัน Kings\r\nCup, President Cup, Merdeka, Djakarta Cup</span></p>\r\n\r\n<p>นอกจากนี้ยังได้รับความร่วมมือจากฟีฟ่าจัดส่งวิทยากรมาช่วยดำเนินการ</p>\r\n\r\n<br></p>', '0000-00-00'),
(26, 'กติกาของฟุตบอล', 'พ33101', '<p>\r\n\r\n<p>กติกาของฟุตบอลมีจำนวนทั้งหมด 17<span> ข้อในปัจจุบันดังนี้</span></p>\r\n\r\n<p>ข้อ 1<span> สนามฟุตบอลจะต้องเป็นสนามหญ้ารูปที่เหลี่ยมผืนผ้า\r\nและมีเส้นในสนามทั้งหมดเป็นสีขาว</span></p>\r\n\r\n<p>ข้อ 2<span> ลูกฟุตบอล ต้องเป็นลูกกลม ทำมาจากหนัง\r\nหรือวัสดุอื่นตามความเหมาะสมและต้องไม่เป็นอันตรายต่อผู้เล่น</span></p>\r\n\r\n<p>ข้อ 3<span> ผู้เล่นทั้งสองทีมต้องมีตัวจริงและตัวสำรอง โดยตัวจริงมี 11 คน และมีตัวสำรองไม่เกิน 7 คน</span></p>\r\n\r\n<p>ข้อ 4<span> ผู้เล่นจะต้องใส่เสื้อทีมสีตัดกันอย่างชัดเจน ยกเว้นผู้รักษาประตู</span></p>\r\n\r\n<p>ข้อ 5<span> ผู้คอยควบคุมการแข่งขันคือผู้ตัดสินหรือกรรมการ\r\nและมีผู้ช่วยผู้ตัดสินให้ความช่วยเหลือ</span></p>\r\n\r\n<p>ข้อ 6<span>\r\nผู้ช่วยผู้ตัดสินมีหน้าที่ในการเป็นผู้คอยชี้แนะหรือให้คำปรึกษาแก่ผู้ตัดสินหรือกรรมการในสนาม</span></p>\r\n\r\n<p>ข้อ 7<span> ระยะเวลาที่ใช้ในการแข่งขันฟุตบอลมี 2\r\nช่วงเวลาด้วยกันคือครึ่งแรก และครึ่งหลัง แต่ละครึ่งมีเวลา 45 นาทีโดยไม่รวมการทดเวลาและพักครึ่งได้ไม่เกิน 15\r\nนาที</span></p>\r\n\r\n<p>ข้อ 8<span> ลูกบอลจะอยู่ในการเล่นเมื่อกระดอนจากเสาประตู คานประตู ธงมุมสนาม\r\nเข้ามาในสนามการแข่งขัน</span></p>\r\n\r\n<p>ข้อ 9<span>\r\nลูกบอลจะอยู่นอกการเล่นเมื่อลูกบอลได้ผ่านเส้นประตูหรือเส้นข้างออกไปหมดทั้งลูก\r\nและเมื่อผู้ตัดสินสั่งหยุดเกม</span></p>\r\n<p>ข้อ 10<span> การนับคะแนนในการยิงเข้า 1 ลูกให้นับได้ว่าคือ 1 แต้มหรือ 1\r\nประตูผู้ชนะคือทีมที่ได้แต้มหรือประตูมากกว่าแต่ถ้าแต้มหรือประตูเท่ากันให้ถือว่าเสมอ</span></p>\r\n\r\n<p>ข้อ 11<span> ตำแหน่งล้ำหน้าคือ ผู้เล่นที่อยู่ในตำแหน่งล้ำหน้าได้สัมผัสกับลูกบอล\r\nโดยผู้ตัดสินเห็นว่ามีส่วนร่วมในการเล่นอย่างชัดเจน\r\nแต่จะไม่ถือว่าล้ำหน้าในกรณีที่ลูกบอลถูกเตะออกมาจากประตูหรือเตะมุม\r\nหรือจากการทุ่ม</span></p>\r\n\r\n<p>ข้อ 12<span> การทำโทษในการแข่งขันฟุตบอลมี 3 แบบคือ ตักเตือน\r\nใบเหลือง และใบแดง</span></p>\r\n\r\n<p>ข้อ 13<span> ฟรีคิก จะเกิดขึ้นเมื่อมีผู้เล่นทำฟาล์วนอกกรอบเขตโทษ\r\nการเตะฟรีคิกผู้เล่นจะนำลุกบอลมาตั้งเตะในจุดที่ผู้เล่นทำฟาล์ว</span></p>\r\n\r\n<p>ข้อ 14<span> ลูกโทษ จะเกิดขึ้นเมื่อมีการทำฟาล์วในกรอบสีเหลี่ยมหน้าประตู\r\nทำให้เสียลูกโทษ</span></p>\r\n\r\n<p>ข้อ 15<span> การทุ่ม คือการโยนลูกบอลเข้าไปในสนามจากเส้นข้างบริเวณที่ออก</span></p>\r\n\r\n<p>ข้อ 16<span> โกลคิก คือ ลูกที่ผู้รักษาประตูตั้งเตะจากเขตประตู ในกรอบเขตประตู</span></p>\r\n\r\n<p>ข้อ 17 <span>การเตะมุม เกิดขึ้นเมื่อลูกบอลทั้งลูกได้ผ่านเส้นประตูออกไปยังนอกสนาม\r\nไม่ว่าจะกลิ้งหรือลอยออกไปนอกสนามก็ตาม\r\nโดยผู้รักษาประตูเป็นคนสัมผัสบอลคนสุดท้ายก่อนลูกออก\r\nเมื่อได้ลูกเตะมุมผู้เตะต้องตั้งบอลในเขตเตะมุม ใกล้กับธงมุมและเมื่อเตะลูกออกจะต้องไม่ทำให้ธงเคลื่อนที่\r\nในการเตะมุมถ้าเตะทีเดียวแล้วลูกเข้าประตูให้นับว่าเป็นประตู\r\nผู้เล่นฝั่งตรงข้ามกับผู้เตะมุมจะเข้าใกล้ผู้เตะมุมน้อยกว่า 10 หลาไม่ได้นอกจากผู้เตะจะเตะลูกไปไกลแล้วอย่างน้อยเท่ากับระยะรอบวงของลูกจึงจะเริ่มเล่นต่อไปได้\r\nและจะเล่นลูกนั้นซ้ำไม่ได้จนกว่าลูกนั้นจะถูกตัวของผู้เล่นคนใดคนหนึ่งก่อน</span></p>\r\n\r\n<br></p>', '0000-00-00'),
(27, 'สรุปวิวัฒนาการของฟุตบอล', 'พ33101', '<p>\r\n\r\n<p><b>สรุปวิวัฒนาการของฟุตบอล</b></p>\r\n\r\n<p>ก่อนคริสตกาล –\r\n<span>อ้างถึงการเล่นเกมซึ่งเปรียบเสมือนต้นฉบับของกีฬาฟุตบอลที่เก่าแก่ที่ได้มีการค้นพบจากการเขียนภาษาญี่ปุ่น-จีน\r\nและในสมัยวรรณคดีของกรีกและโรมัน</span></p>\r\n\r\n<p></p>\r\n\r\n<p>ยุคกลาง –\r\n<span>ประวัติบันทึกการเล่นในเกาะอังกฤษ อิตาลี และฝรั่งเศส</span></p>\r\n\r\n<p>ปี พ.ศ. 1857\r\n– <span>พระเจ้าเอ๊ดเวิร์ดที่ 3\r\nทรงออกพระราชกฤษฎีกาห้ามเล่นฟุตบอล เพราะจะรบกวนการยิงธนู</span></p>\r\n\r\n<p>ปี พ.ศ. 2104\r\n– Richardo Custor <span>อาจารย์สอนหนังสือชาวอังกฤษกล่าวถึงการเล่นว่า\r\nควรกำหนดไว้ในบทเรียนของเยาวชน โดยได้รับอิทธิพลจาการเล่นกาลซิโอในเมืองฟลอเร้นท์</span></p>\r\n\r\n<p>ปี พ.ศ. 2123\r\n-Riovanni Party <span>ได้จัดพิมพ์กติการการเล่นคาลซิโอ</span></p>\r\n\r\n<p>ปี พ.ศ. 2223\r\n-<span>ฟุตบอลในประเทศอังกฤษได้รับพระบรมราชานุเคราะห์จากพระเจ้ชาร์ลที่ 2</span></p>\r\n\r\n<p>ปี พ.ศ. 2391\r\n-<span>ได้มีการเขียนกฎข้อบังคับเคมบริดจ์ขึ้นเป็นครั้งแรก</span></p>\r\n\r\n<p>ปี พ.ศ. 2406\r\n-<span>ได้มีการก่อตั้งสมาคมฟุตบอลขึ้น</span></p>\r\n\r\n<p>ปี พ.ศ. 2426\r\n-<span>สมาคมฟุตบอลจักรภพ 4 แห่ง ยอมรับองค์กรควบคุม\r\nและจัดตั้งกรรมการระหว่างชาติ</span></p>\r\n\r\n<p>ปี พ.ศ. 2429\r\n-<span>สมาคมฟุตบอลเริ่มทำการฝึกเจ้าหน้าที่ที่จัดการแข่งขัน</span></p>\r\n\r\n<p>ปี พ.ศ. 2431\r\n-<span>เริ่มเปิดการแข่งขันฟุตบอลลีก โดยยินยอมให้มีนักฟุตบอลอาชีพ\r\nและเพิ่มอำนาจการควบคุมให้ผู้ตัดสิน</span></p>\r\n\r\n<p>ปี พ.ศ. 2432\r\n-<span>สมาคมฟุตบอลส่งทีมไปแข่งขันในต่างประเทศ เช่น เยอรมันไปเยือนอังกฤษ</span></p>\r\n\r\n<p>ปี พ.ศ. 2447\r\n– <span>ก่อตั้งฟีฟ่า ซึ่งมีสำนักงานอยู่ที่กรุงปารีส เมื่อ 11 พฤษภาคม พ.ศ. 2447 โดยสมาคมแห่งชาติคือ ฝรั่งเศส\r\nเบลเยียม เดนมาร์ก สเปน สวีเดน และสวิตเซอร์แลนด์</span></p>\r\n\r\n<p>ปี พ.ศ. 2480\r\n– 2481 -<span>ข้อบังคับปัจจุบันเขียนขึ้นตามระบบใหม่ขององค์กรควบคุม\r\nโดยใช้ข้อบังคับเก่ามาเป็นแนวทาง</span></p>\r\n\r\n<br></p>', '0000-00-00'),
(28, 'ประวัติยุคก่อนอาณาจักรสุโขทัย', 'ส 31101', '<p>\r\n\r\n<p>เดิมที สุโขทัย เป็นสถานีการค้าของแคว้นละโว้\r\n(ลวรัฐ) ของอาณาจักรขอม บนเส้นทางการค้าผ่านคาบสมุทรระหว่างอ่าวเมาะตะมะ\r\nกับเขตที่ราบลุ่มแม่น้ำโขงตอนกลาง (ประเทศลาว)\r\nคาดว่าเริ่มตั้งเป็นสถานีการค้าในราวพุทธศักราช 1700 ในรัชสมัยของพระยาธรรมิกราช\r\nกษัตริย์ละโว้ โดยมีพ่อขุนศรีนาวนำถม เป็นผู้ปกครองและดูแลกิจการภายในเมืองสุโขทัย\r\nและศรีสัชนาลัย ต่อมาเมื่อพ่อขุนศรีนาวนำถมสวรรคต ขอมสบาดโขลญลำพง\r\nซึ่งเป็นคล้ายๆกับผู้ตรวจราชการจากลวรัฐ เข้าทำการยึดอำนาจการปกครองสุโขทัย\r\nจึงส่งผลให้ พ่อขุนผาเมือง (พระราชโอรสของพ่อขุนศรีนาวนำถม) เจ้าเมืองราด และ\r\nพ่อขุนบางกลางหาว เจ้าเมืองบางยาง ตัดสินพระทัยจะยึดดินแดนคืน\r\nการชิงเอาอำนาจจากผู้ครองเดิมคือ อาณาจักรขอม เมื่อปี พ.ศ. 1781 และสถาปนาเอกราช\r\nให้กรุงสุโขทัยขึ้นเป็นราชธานีของชาวไทย โดยไม่ขึ้นตรงกับรัฐใด</p>\r\n\r\n<p>และพ่อขุนผาเมือง ก็กลับยกเมืองสุโขทัย ให้พ่อขุนบางกลางหาวครอง\r\nพร้อมทั้ง พระแสงขรรค์ชัยศรี และพระนาม กำมรเตงอัญศรีอินทรบดินทราทิตย์\r\nซึ่งพระเจ้าชัยวรมันที่ 7 ทรงพระราชทานให้พ่อขุนผาเมืองก่อนหน้านี้ โดยคาดว่า\r\nเหตุผลคือพ่อขุนผาเมืองมีพระนางสิขรเทวีพระมเหสี (ราชธิดาของพระเจ้าชัยวรมันที่ 7)\r\nซึ่งพระองค์เกรงว่าชาวสุโขทัยจะไม่ยอมรับ\r\nแต่ก็กลัวว่าทางขอมจะไม่ไว้ใจจึงมอบพระนามพระราชทาน และพระแสงขรรค์ชัยศรี\r\nขึ้นบรมราชาภิเษก พ่อขุนบางกลางหาว ให้เป็นกษัตริย์ เพื่อเป็นการตบตาราชสำนักขอม</p>\r\n\r\n<br></p>', '0000-00-00');
INSERT INTO `content` (`conid`, `contitle`, `subid`, `contxt`, `contime`) VALUES
(30, 'อาณาจักรสุโขทัย', 'ส 31101', '\r\n\r\n<p>อาณาจักรสุโขทัย</p>\r\n\r\n<p>หลังจากมีการสถาปนาอาณาจักรสุโขทัยขึ้นเป็นราชธานี\r\nและมีพ่อขุนศรีอินทราทิตย์ เป็นปฐมกษัตริย์แล้ว พระองค์ทรงดูแลพระราชอาณาจักร\r\nและบำรุงราษฏรเป็นอย่างดี</p>\r\n\r\n<p>พระมหากษัตริย์พระองค์ที่สาม\r\nพ่อขุนรามคำแหงมหาราช ทรงพระปรีชาสามารถทั้งในด้านนิรุกติศาสตร์ การปกครอง กฎหมาย\r\nวิศวกรรม ศาสนา ความสัมพันธ์ระหว่างประเทศ เป็นต้น ผลงานของพระองค์ที่ปรากฏให้เห็น\r\nอาทิ ศิลาจารึกที่ค้นพบในสมัยของพระบาทสมเด็จพระนั่งเกล้าเจ้าอยู่หัว\r\nที่อธิบายถึงความเป็นมา ลีลาชีวิตของชาวสุโขทัยโบราณ น้ำพระทัยของพระมหากษัตริย์\r\nการพิพากษาอรรถคดี ฯลฯ นอกจากนี้ยังมีผลงานทางวิศวกรรมชลประทาน คือ\r\nเขื่อนสรีดภงค์ที่เป็นการกักเก็บน้ำไว้ใช้ในยามแล้ง\r\nมีการทำท่อส่งน้ำจากตัวเขื่อนมาใช้ในเมือง</p>\r\n\r\n<p>พระมหากษัตริย์ที่ทรงทำนุบำรุงศาสนามากที่สุดคือ\r\nพระเจ้าลิไท ในรัชสมัยของพระองค์มีการสร้างวัดมากที่สุด\r\nกษัตริย์พระองค์สุดท้ายในฐานะรัฐอิสระ คือ พระมหาธรรมราชาที่ 4 (บรมปาล)\r\nต่อจากนั้น อาณาจักรได้ถูกแบ่งส่วนออกเป็นของอาณาจักรอยุธยา และอาณาจักรล้านนา\r\nจนในที่สุด อาณาจักรทั้งหมด ก็ถูกรวมศูนย์\r\nเข้าเป็นดินแดนสวนหนึ่งของอาณาจักรอยุธยา</p>\r\n<p>ความเจริญรุ่งเรือง</p>\r\n\r\n<p>ด้านเศรษฐกิจ</p>\r\n\r\n<p>สภาพเศรษฐกิจสมัยสุโขทัยเป็นระบบเศรษฐกิจแบบเสรีนิยม\r\nดังข้อความปรากฏในหลักศิลาจารึกหลักที่ 1 "…ใครจักใคร่ค้าช้างค้า\r\nใครจักใคร่ค้าม้าค้า ใครจักใคร่ค้าเงินค้าทองค้า…" และ\r\n"...เมืองสุโขทัยนี้ดี ในน้ำมีปลาในนามีข้าว..."\r\nประชาชนประกอบอาชีพเกษตรกรรมด้วยระบบการเกษตรแบบพึ่งพาธรรมชาติ\r\nเช่นสังคมไทยส่วนใหญ่ในชนบทปัจจุบัน</p>\r\n\r\n<p>ด้านสังคม ความเชื่อ และศาสนา</p>\r\n\r\n<p>การใช้ชีวิตของผู้คนในสมัยสุโขทัยมีความอิสระเสรี\r\nมีเสรีภาพอย่างมากเนื่องจากผู้ปกครองรัฐให้อิสระแก่ไพร่ฟ้า\r\nและปกครองผู้ใต้ปกครองแบบพ่อกับลูก ดังปรากฏหลักฐานในศิลาจารึกว่า "…ด้วยเสียงพาทย์\r\nเสียงพิณ เสียงเลื่อน เสียงขับ ใครจักมักเล่น เล่น ใครจักมักหัว หัว\r\nใครจักมักเลื่อน เลื่อน…"</p>\r\n\r\n<p>ด้านความเชื่อและศาสนา\r\nสังคมยุคสุโขทัยประชาชนมีความเชื่อทั้งเรื่องวิญญาณนิยม<span>&nbsp; (</span>Animism) ไสยศาสตร์\r\nศาสนาพราหมณ์ฮินดู และพุทธศาสนา ดังปรากฏหลักฐานในศิลาจารึกหลักที่ 1 ด้านที่ 3\r\nว่า "…เบื้องหัวนอนเมืองสุโขทัยนี้มีกุฎิวิหารปู่ครูอยู่\r\nมีสรีดพงส์ มีป่าพร้าว ป่าลาง ป่าม่วง ป่าขาม มีน้ำโคก มีพระขระพุงผี\r\nเทพยาดาในเขาอันนั้นเป็นใหญ่กว่าทุกผีในเมืองนี้ ขุนผู้ใดถือเมืองสุโขทัยนี้แล้ว\r\nไหว้ดีพลีถูก เมืองนี้เที่ยว เมืองนี้ดี ผิไหว้บ่ดี พลีบ่ถูก\r\nผีในเขาอันนั้นบ่คุ้มบ่เกรง เมืองนี้หาย…"</p>\r\n\r\n<p>ส่วนด้านศาสนา\r\nได้รับอิทธิพลจากพุทธศาสนานิกายเถรวาทแบบลังกาวงศ์จากนครศรีธรรมราช ในวันพระ\r\nจะมีภิกษุเทศนาสั่งสอน<span>&nbsp; ณ\r\nลานธรรมในสวนตาล&nbsp; โดยใช้&nbsp; พระแท่นมนังคศิลาอาสน์&nbsp; เป็นอาสนะสงฆ์ ในการบรรยายธรรมให้ประชาชนฟัง\r\nยังผลให้ประชาชนในยุคนี้นิยมปฏิบัติตนอยู่ในศีลธรรม มีการถือศีล\r\nโอยทานกันเป็นปกติวิสัย ทำให้สังคมโดยรวมมีความสงบสุขร่มเย็น&nbsp; </span></p>\r\n\r\n<p>ด้านการปกครอง</p>\r\n\r\n<p>ด้านการปกครองสามารถแยกกล่าวเป็น 2 แนว ดังนี้</p>\r\n\r\n<p>ในแนวราบ </p>\r\n\r\n<p>จัดการปกครองแบบพ่อปกครองลูก\r\nกล่าวคือผู้ปกครองจะมีความใกล้ชิดกับประชาชน ให้ความเป็นกันเองและความยุติธรรมกับประชาชนเป็นอย่างมาก\r\nเมื่อประชาชนเกิดความเดือดร้อนไม่ได้รับความเป็นธรรม\r\nสามารถร้องเรียนกับพ่อขุนโดยตรงได้\r\nโดยไปสั่นกระดิ่งที่แขวนไว้ที่หน้าประตูที่ประทับ ดังข้อความในศิลาจารึกปรากฏว่า\r\n"…ในปากประตูมีกระดิ่งอันหนึ่งไว้หั้น\r\nไพร่ฟ้าหน้าใส…" นั่นคือเปิดโอกาสให้ประชาชนสามารถมาสั่นกระดิ่งเพื่อแจ้งข้อร้องเรียนได้</p>\r\n<p>ในแนวดิ่ง </p>\r\n\r\n<p>ได้มีการจัดระบบการปกครองขึ้นเป็น 4 ชนชั้น\r\nคือ<span>&nbsp; </span></p>\r\n\r\n<p>พ่อขุน เป็นชนชั้นผู้ปกครอง\r\nอาจเรียกชื่ออย่างอื่น เช่น เจ้าเมือง พระมหาธรรมราชา หากมีโอรสก็จะเรียก\r\n"ลูกเจ้า" </p>\r\n\r\n<p>ลุกขุน เป็นข้าราชบริพาร ข้าราชการที่มีตำแหน่งหน้าที่ช่วงปกครองเมืองหลวง\r\nหัวเมืองใหญ่น้อย และภายในราชสำนัก\r\nเป็นกลุ่มคนที่ใกล้ชิดและได้รับการไว้วางใจจากเจ้าเมืองให้ปฏิบัติหน้าที่บำบัดทุกข์บำรุงสุขแก่ไพร่ฟ้า\r\n</p>\r\n\r\n<p>ไพร่หรือสามัญชน\r\nได้แก่ราษฎรทั่วไปที่อยู่ในราชอาณาจักร (ไพร่ฟ้า) </p>\r\n\r\n<p>ทาส ได้แก่ชนชั้นที่ไม่มีอิสระในการดำรงชีวิตอย่างสามัญชนหรือไพร่\r\n(อย่างไรก็ตามประเด็นทาสนี้ยังคงถกเถียงกันอยู่ว่ามีหรือไม่) </p>\r\n\r\n<p>ความสัมพันธ์กับต่างชาติ</p>\r\n\r\n<p>จักรวรรดิมองโกล</p>\r\n\r\n<p>กองทัพจักรวรรดิมองโกลแผ่แสนยานุภาพโดดเด่นที่สุดเป็นช่วงเดียวกับการตั้งกรุงสุโขทัย\r\nในปี พ.ศ. 1800 (ค.ศ. 1257) ซึ่งเป็นอาณาจักรของตนอย่างแท้จริงเป็นครั้งแรก</p>\r\n\r\n<p>หลักฐานสำคัญในพงศาวดารหงวนฉบับเก่า เล่มที่ 2\r\nแปลเรื่องราวการติดต่อระหว่างอาณาจักรสุโขทัยกับราชวงศ์มองโกล\r\nได้สรุปไว้ว่ากุบไลข่านทรงปรึกษาขุนนางข้าราชการระดับสูงเกี่ยวกับการเตรียมทัพไปปราบปรามแคว้นต่างๆ\r\nทางใต้ มีสุโขทัย ละโว้ สุมาตรา และอื่นๆ เป็นเมืองขึ้น ปรากฏว่าขุนนางชื่อ เจี่ย\r\nหลู่ น่าต๋าไม่เห็นด้วยและได้กราบบังคมทูลเสนอแนะให้ทรงชักชวนให้ผู้นำดินแดนต่างๆ\r\nอ่อนน้อมยอมสนับสนุนก่อน หากไม่ยอมจึงยกกองทัพไปโจมตี\r\nนี่คือเหตุผลประการหนึ่งที่กุบไลข่านทรงส่งคณะทูตไปเจริญสัมพันธไมตรี\r\nและขอให้ส่งเครื่องราชบรรณาการไปยังราชสำนักมองโกล\r\nเพื่อแสดงความจงรักภักดีต่ออาณาจักรมองโกล ปราฏว่ามีอาณาจักรในดินแดนต่างๆ กว่า 20\r\nอาณาจักรยอมรับข้อเสนอ รวมทั้งอาณาจักรสุโขทัยด้วย (ช่วงระหว่างประมาณ พ.ศ. 1822 -\r\n1825)<span>&nbsp; </span></p>\r\n\r\n<p>พงศาวดารหงวนฉบับเก่า เล่มที่ 12\r\nเป็นหลักฐานสำคัญที่กล่าวถึงคณะทูตชุดแรกจากอาณาจักรมองโกลในสมัยกุบไลข่าน\r\nเดินทางมายังอาณาจักรสุโขทัยในเดือนพฤศจิกายนปี พ.ศ. 1825 (ค.ศ. 1282)\r\nทูตคณะนี้นำโดยเหอจี จี่ นายทหารระดับสูงเป็นหัวหน้าคณะ\r\nแต่ขณะนั่งเรือแล่นผ่านฝั่งทะเลอาณาจักรจามปา ได้ถูกจับกุมและถูกประหารชีวิต\r\nผลจากคณะทูตนี้ถูกประหารชีวิตก่อนจะเดินทางไปยังอาณาจักรสุโขทัยทำให้อาณาจักรสุโขทัยไม่ทราบว่ามองโกลพยายามส่งทูตมาติดต่อ</p>\r\n\r\n<p>พงศาวดารหงวนฉบับเก่า เล่มที่ 17\r\nกล่าวถึงคณะทูตมองโกลชุดที่สองเดินทางมายังอาณาจักรสุโขทัยในปี พ.ศ. 1835 (ค.ศ.\r\n1292) ภายหลังจากข้าหลวงใหญ่ฝ่ายรักษาความสงบเรียบร้อยของมณฑลกวางตุ้ง\r\nได้ส่งคนอัญเชิญพระราชสาส์นอักษรทองคำของกษัตริย์แห่งอาณาจักรสุโขทัยไปยังนครหลวงข่านมาลิก\r\n(ต้าตู หรือปักกิ่งปัจจุบัน)\r\nคณะทูตมองโกลชุดที่สองได้อัญเชิญพระบรมราชโองการของกุบไลข่านให้พ่อขุนรามคำแหงเสร็จไปเฝ้า\r\nพระบรมราชโองการนี้แสดงให้เห็นนโยบายของอาณาจักรมองโกลเรียกร้องให้ผู้นำของอาณาจักรต่างๆ\r\nไปเฝ้ากุบไลข่าน แต่มิได้บังคับให้เป็นไปตามนี้\r\nซึ่งจะเห็นได้ว่าพ่อขุนรามคำแหงก็มิได้ปฏิบัติตามแต่ประการใด</p>\r\n\r\n<p>พงศาวดารหงวนฉบับเก่า<span>&nbsp; เล่มที่&nbsp;\r\n18&nbsp; กุบไลข่านได้ส่งคณะทูตชุดที่สามมาสุโขทัย&nbsp;\r\nโดยได้อัญเชิญพระบรมราชโองการให้พ่อขุนรามคำแหงเสด็จไปเฝ้า\r\nหากมีเหตุขัดข้องให้ส่งโอรสหรือพระอนุชาและอำมาตย์ผู้ใหญ่เป็นตัวประกัน&nbsp; ซึ่งปรากฏว่าพ่อขุนรามคำแหงก็มิได้ปฏิบัติตาม\r\nแต่ส่งคณะทูตนำเครื่องราชบรรณาการไปแทน</span></p>\r\n\r\n<p>อาณาจักรล้านนา</p>\r\n\r\n<p>ในปี พ.ศ. 1839 พญามังราย<span>&nbsp; (พ.ศ. 1804 - 1854)&nbsp;\r\nได้มีคำสั่งให้สร้างเมืองใหม่ขึ้นมาโดยใช้ชื่อว่าเจียงใหม่ (เชียงใหม่)\r\nเพื่อที่จะเป็นเมืองหลวงแห่งใหม่ของอาณาจักรล้านนา ครั้งนั้นพ่อขุนรามคำแหงมหาราชและพญางำเมือง\r\nได้เสด็จมาช่วยด้วย</span></p>\r\n\r\n<p>อาณาจักรอยุธยา</p>\r\n\r\n<p>หลังจากมีการก่อตั้งกรุงศรีอยุธยา\r\nแรกนั้นสุโขทัยและอยุธยาไม่ได้เป็นไมตรีต่อกัน แต่ด้วยชัยภูมิที่เหมาะสมกว่า\r\nทำให้อยุธยาเจริญเติบโตอย่างรวดเร็ว\r\nประกอบกับปัญหาการเมืองภายในของสุโขทัยมิได้เป็นไปโดยสงบ\r\nมีการแย่งชิงราชสมบัติกันระหว่าง พระยาบานเมือง พระยาราม\r\nยังผลให้อยุธยาสบโอกาสเข้าแทรกแซงกิจการภายใน\r\nในรัชกาลนี้มีการรับไมตรีจากอยุธยาโดยการสมรสระหว่างราชวงศ์พระร่วง\r\nกับราชวงศ์สุพรรณภูมิ โดยมีพระราเมศวร\r\nซึ่งต่อมาคือสมเด็จพระบรมไตรโลกนาถเป็นราชบุตรจากสองราชวงศ์</p>\r\n\r\n<p>จนสิ้นรัชกาลพระมหาธรรมราชาที่ 4 (บรมปาล)\r\nพระยายุทธิษฐิระซึ่งเดิมทีอยู่ศรีสัชนาลัย ได้เข้ามาครองเมืองสุโขทัย\r\nและเมื่อแรกที่สมเด็จพระบรมไตรโลกนาถ เสด็จขึ้นผ่านภิภพ\r\nเป็นพระมหากษัตริย์กรุงศรีอยุธยา ปรากฏว่าขณะนั้น พระยายุทธิษฐิระ\r\nเกิดความน้อยเนื้อต่ำใจ ที่ได้เพียงตำแหน่งพระยาสองแคว เนื่องด้วย\r\nสมเด็จพระบรมไตรโลกนาถทรงเคยดำริไว้สมัยทรงพระเยาว์ว่า\r\nหากได้ขึ้นเป็นพระมหากษัตริย์\r\nจะชุบเลี้ยงพระยายุทธิษฐิระให้ได้เป็นพระร่วงเจ้าสุโขทัย พ.ศ. 2011\r\nพระยายุทธิษฐิระจึงเอาใจออกห่างจากสมเด็จพระบรมไตรโลกนาถ ไปขึ้นกับ พระยาติโลกราช\r\nกษัตริย์ล้านนาในขณะนั้น เหตุการณ์นี้ส่งผลให้เกิดการเฉลิมพระนามกษัตริย์ล้านนา จากพระยา\r\nเป็น<span>&nbsp; พระเจ้า\r\nเพื่อให้เสมอศักดิ์ด้วยกรุงศรีอยุธยา&nbsp;\r\nพระนามพระยาติโลกราช จึงได้รับการเฉลิมเป็นพระเจ้าติโลกราช</span></p>\r\n\r\n<p>หลังจากที่พระยายุทธิษฐิระ\r\nนำสุโขทัยออกจากอยุธยาไปขึ้นกับล้านนา<span>&nbsp;\r\nสมเด็จพระบรมไตรโลกนาถจึงทรงเสด็จจากกรุงศรีอยุธยา กลับมาพำนัก&nbsp; ณ เมืองสรลวงสองแคว\r\nพร้อมทั้งสร้างกำแพงและค่ายคู ประตู หอรบ แล้วจึงสถาปนาขึ้นเป็นเมือง\r\nพระพิษณุโลกสองแคว เป็นราชธานีฝ่ายเหนือของอาณาจักรแทนสุโขทัย ในเวลาเจ็ดปีให้หลัง\r\nสมเด็จพระบรมไตรโลกนาถจึงทรงตีเอาสุโขทัยคืนได้\r\nแต่เหตุการณ์ทางเมืองเหนือยังไม่เข้าสู่ภาวะที่น่าไว้วางใจ\r\nจึงทรงตัดสินพระทัยพำนักยังนครพระพิษณุโลกสองแควต่อจนสิ้นรัชกาล ส่วนทางอยุธยานั้น\r\nทรงได้สถาปนาสมเด็จพระบรมราชาธิราชที่ 3 พระราชโอรส เป็นพระมหาอุปราช\r\nดูแลอยุธยาและหัวเมืองฝ่ายใต้</span></p>\r\n\r\n<p>ด้วยความที่เป็นคนละประเทศมาก่อน<span>&nbsp; และมีสงครามอยู่ด้วยกัน\r\nชาวบ้านระหว่างสุโขทัยและอยุธยา จึงมิได้ปรองดองเป็นน้ำหนึ่งใจเดียวกัน&nbsp; จึงต้องแยกปกครอง&nbsp; โดยพระมหากษัตริย์อยุธยา\r\nจะทรงสถาปนาพระราชโอรส หรือพระอนุชา หรือพระญาติ อันมีเชื้อสายสุโขทัย\r\nปกครองพิษณุโลกในฐานะราชธานีฝ่ายเหนือ และควบคุมหัวเมืองเหนือทั้งหมด</span></p>\r\n\r\n<p>พ.ศ. 2127<span>&nbsp;\r\nหลังจากชนะศึกที่แม่น้ำสะโตงแล้ว&nbsp;\r\nพระนเรศวรโปรดให้เทครัวเมืองเหนือทั้งปวง (ตาก สุโขทัย ศรีสัชนาลัย\r\nพิษณุโลก กำแพงเพชร ชัยบุรี ศรีเทพ) ลงมาไว้ที่อยุธยา เพื่อเตรียมรับศึกใหญ่\r\nพิษณุโลกและหัวเมืองเหนือทั้งหมดจึงกลายเป็นเมืองร้าง หลังจากเทครัวไปเมืองใต้\r\nจึงสิ้นสุดการแบ่งแยกระหว่างชาวเมืองเหนือ กับชาวเมืองใต้\r\nและถือเป็นการสิ้นสุดของรัฐสุโขมัยโดยสมบูรณ์ เพราะหลังจากนี้ 8 ปี\r\nพิษณุโลกได้ถูกฟื้นฟูอีครั้ง แต่ถือเป็นเมืองเอกในราชอาณาจักร\r\nมิใช่ราชธานีฝ่ายเหนือ</span></p>\r\n\r\n<p>ในด้านวิชาการ\r\nมีนักวิชาการหลายท่านได้เสนอเพิ่มว่า เหตุการณ์อีกประการ อันทำให้ต้องเทครัวเมืองเหนือทั้งปวงโดยเฉพาะพิษณุโลกนั้น\r\nอยู่ที่เหตุการณ์แผ่นดินไหวครั้งใหญ่ บนรอยเลื่อนวังเจ้า ในราวพุทธศักราช 2127\r\nแผ่นดินไหวครั้งนี้ส่งผลให้ตัวเมืองพิษณุโลกราพณาสูญ แม้แต่แม่น้ำแควน้อย\r\nก็เปลี่ยนเส้นทางไม่ผ่านเมืองพิษณุโลก แต่ไปบรรจบกับแม่น้ำโพ (ปัจจุบันคือแม่น้ำน่าน)\r\nที่เหนือเมืองพิษณุโลกขึ้นไป และยังส่งผลให้พระศรีรัตนมหาธาตุพิษณุโลก\r\nหักพังทลายในลักษณะที่บูรณะคืนได้ยาก\r\nในการฟื้นฟูจึงกลายเป็นการสร้างพระปรางค์แบบอยุธยาครอบทับลงไปแทน</p>\r\n\r\n<p>รายพระนามพระมหากษัตริย์สุโขทัย</p>\r\n\r\n<p>ราชวงศ์นำถุม (ราชวงศ์ผาเมือง) </p>\r\n\r\n<p>พ่อขุนศรีนาวนำถุม ครองราชย์ปีใดไม่ปรากฏ -\r\nพ.ศ. 1724 </p>\r\n\r\n<p>ขอมสบาดโขลญลำพง </p>\r\n\r\n<p>ขอมสบาดโขลญลำพง (พ.ศ. 1724 - พ.ศ. 1780) </p>\r\n\r\n<p>ราชวงศ์พระร่วง<span>&nbsp; </span></p>\r\n\r\n<p>พ่อขุนศรีอินทราทิตย์ (พ.ศ. 1780-\r\nสวรรคตปีใดไม่ปรากฏ (ประมาณ พ.ศ. 1801) ) </p>\r\n\r\n<p>พ่อขุนบานเมือง\r\n(หลังพ่อขุนศรีอินทราทิตย์สวรรคต - พ.ศ. 1822) </p>\r\n\r\n<p>พ่อขุนรามคำแหงมหาราช<span>&nbsp; (พ.ศ. 1822 - พ.ศ. 1842)&nbsp; (ในพงศาวดารกรุงศรีอยุธยา เรียกว่า\r\nพ่อขุนรามราช) </span></p>\r\n\r\n<p>ปู่ไสสงคราม (รักษาราชการชั่วคราวแทน พญาเลอไท\r\nซึ่งขณะนั้นไม่ได้อยู่ในเมืองสุโขทัย) </p>\r\n\r\n<p>พญาเลอไท (พ.ศ. 1842 - พ.ศ. 1833) </p>\r\n\r\n<p>ราชวงศ์นำถุม </p>\r\n\r\n<p>พญางั่วนำถุม (พ.ศ. 1833 - พ.ศ. 1890) </p>\r\n\r\n<p>ราชวงศ์พระร่วง<span>&nbsp; </span></p>\r\n\r\n<p>พระมหาธรรมราชาที่ 1 (ลิไท) (พ.ศ. 1890 - พ.ศ.\r\n1913) </p>\r\n\r\n<p>พระมหาธรรมราชาที่ 2 (ลือไท) (พ.ศ. 1913 - พ.ศ.\r\n1931) (ตกเป็นประเทศราชของอยุธยาในปี พ.ศ. 1921) </p>\r\n\r\n<p>พระมหาธรรมราชาที่ 3 (ไสยลือไท) (พ.ศ. 1931 -\r\nพ.ศ. 1962) </p>\r\n\r\n<p>พระมหาธรรมราชาที่ 4 (บรมปาล) (พ.ศ. 1962 -\r\nพ.ศ. 1981) </p>\r\n\r\n<p>พระยายุทธิษฐิระ (พ.ศ. 1991 - พ.ศ. 2011)\r\n(เป็นประเทศราชล้านนาในปี พ.ศ. 2011) [2] </p>\r\n\r\n<p>ราชวงศ์สุพรรณภูมิ </p>\r\n\r\n<p>สมเด็จพระบรมไตรโลกนาถ (พ.ศ. 2011 - พ.ศ. 2031)\r\n(สถาปนา และประทับ ณ พิษณุโลก จนสิ้นรัชกาล) </p>\r\n\r\n<p>พระเชษฐาธิราช (พ.ศ. 2031 - พ.ศ. 2034)\r\n(ตำแหน่งพระมหาอุปราช) </p>\r\n\r\n<p>พระอาทิตยวงศ์ (พระหน่อพุทธางกูร) (พ.ศ. 2034 -\r\nพ.ศ. 2072) (ตำแหน่งพระมหาอุปราช) </p>\r\n\r\n<p>พระไชยราชา (พ.ศ. 2072 - พ.ศ. 2077)\r\n(ตำแหน่งพระมหาอุปราช) </p>\r\n\r\n<p>ราชวงศ์สุโขทัย </p>\r\n\r\n<p>พระมหาธรรมราชา (ขุนพิเรนทรเทพ) (พ.ศ. 2077 -\r\nพ.ศ. 2111) (เจ้าราชธานีฝ่ายเหนือ) </p>\r\n\r\n<p>พระนเรศวร (หลังเสด็จกลับจากหงสาวดี - พ.ศ.\r\n2127) (ตำแหน่งพระมหาอุปราช) </p>\r\n\r\n<p>อ้างอิงและหมายเหตุ</p>\r\n\r\n<p>^ พญางั่วนำถุม มาจากสายราชวงศ์นำถุม(ผาเมือง)\r\nแต่ในตำราประวัติศาสตร์ยังถือว่าท่านเป็น "ราชวงศ์พระร่วง" (อภิปราย) </p>\r\n\r\n<p>^ พระยายุทธิษฐิระ ครองศรีสัชนาลัย\r\nซึ่งขณะนั้นมีฐานะเป็นเมืองสำคัญเหนือกว่าพิษณุโลก </p>\r\n\r\n<p>^ ราชการสงครามในสมัยสมเด็จพระนเรศวร หอมรดกไทย\r\nกองทัพบก </p>\r\n<p>พ่อขุนรามคำแหงมหาราช</p>\r\n<p></p>\r\n\r\n<p>ข้อมูลส่วนพระองค์</p>\r\n\r\n<p>พระปรมาภิไธย</p>\r\n\r\n<p>พ่อขุนรามคำแหงมหาราช</p>\r\n\r\n<p>พระอิสริยยศ<span>&nbsp; พระมหากษัตริย์แห่งอาณาจักรสุโขทัย</span></p>\r\n<p>พระราชบิดา<span>&nbsp; พ่อขุนศรีอินทราทิตย์</span></p>\r\n<p>พระราชมารดา<span>&nbsp; พระนางเสือง</span></p>\r\n\r\n<p><span>&nbsp; </span></p>\r\n\r\n<p>การครองราชย์</p>\r\n\r\n<p>ราชวงศ์<span>&nbsp; ราชวงศ์พระร่วง</span></p>\r\n<p>ทรงราชย์<span>&nbsp; พ.ศ.\r\n1822 - พ.ศ. 1860 (โดยประมาณ) </span></p>\r\n\r\n<p>ระยะเวลาครองราชย์<span>&nbsp; 40 ปี (โดยประมาณ)</span></p>\r\n\r\n<p>รัชกาลก่อนหน้า<span>&nbsp; พ่อขุนบานเมือง</span></p>\r\n<p>รัชกาลถัดมา<span>&nbsp; พญาไสสงคราม</span></p>\r\n<p>พ่อขุนรามคำแหง หรือ พ่อขุนรามคำแหงมหาราช\r\nกษัตริย์องค์ที่ 3 แห่งกรุงสุโขทัยในราชวงศ์พระร่วง\r\nเป็นโอรสของพ่อขุนศรีอินทราทิตย์ และพระนางเสือง\r\nทรงเป็นผู้ประดิษฐ์อักษรไทยที่ปรากฏในศิลาจารึกหลักที่ 1</p>\r\n\r\n<p>ทรงเป็นนักรบ</p>\r\n\r\n<p>ความเป็นนักรบของพระองค์นั้น\r\nเห็นได้ตั้งแต่ยังมิได้ครองราชย์ พระองค์ทรงได้รับชนะในการกระทำยุทธหัตถีกับขุนสามชน\r\nเจ้าเมืองฉอด ในสมัยพ่อขุนศรีอินทราทิตย์ พระราชบิดา\r\nและทรงเป็นจอมทัพในการทำสงครามขยายอาณาเขตตลอดรัชสมัยของพระบิดา และพ่อขุนบานเมือง</p>\r\n\r\n<p>ทรงนักปกครอง</p>\r\n\r\n<p>พระองค์ทรงมีพระเมตตาเอาพระทัยใส่ในทุกข์สุขของประชาชน\r\nโดยการสนับสนุนการประกอบอาชีพอย่างเสรี ทรงยกเลิกจังกอบ\r\nซึ่งเป็นภาษีที่คิดตามความกว้างของเรือ ให้กรรมสิทธิที่ดินทำกิน\r\nตลอดจนเสรีภาพให้แก่ราษฎร</p>\r\n\r\n<p>ทรงเป็นนักการทูตที่หลักแหลม</p>\r\n\r\n<p>พระองค์มีนโยบายกระชับมิตรกับดินแดนต่างๆทั้งที่ใกล้เคียง\r\nและแม้แต่ดินแดนอันห่างไกลที่มีอำนาจ เช่น ล้านนา พะเยา ศิริธรรมนคร\r\n(นครศรีธรรมราช) ตลอดจนถึงจีน\r\nนอกจากนี้ยังปรากฏทรงให้ความช่วยเหลือสนับสนุนรัฐที่มาพึ่งพระบรมโพธิสมภารด้วย\r\nเช่น มอญ และล้านช้างเป็นต้น</p>\r\n\r\n<p>ทรงเป็นนักปราชญ์</p>\r\n\r\n<p>พระองค์นั้นทรงเห็นความสำคัญของพระศาสนา\r\nพระองค์ทรงเป็นอัครศาสนูปถัมภก และสนับสนุนทำนุบำรุงพระพุทธศาสนา ดังปรากฏว่า\r\nพระองค์ทรงนิมนต์พระเถระจากเมืองศิริธรรมนคร\r\nมาสถาปนาพระพุทธศาสนาลัทธิลังกาวงศ์ในสุโขทัย\r\nทรงจัดสร้างพระอารามทั้งในและนอกราชธานี\r\nรวมทั้งทรงสนับสนุนการเผยแผ่หลักธรรมและการสร้างศาสนวัตถุอีกด้วย\r\nนอกจากพระราชกรณียกิจทางด้านพระศาสนาแล้วยังมีความสำคัญเช่นกันคือ\r\nทรงส่งเสริมความเจริญทางภูมิปัญญาศิลปวัฒนธรรมที่โดดเด่น คือ การประดิษฐ์อักษรไทย\r\nหรือ ลายสือไทย เป็นหลักฐานสำคัญที่แสดงถึงการประดิษฐ์ลายสือไทย\r\nจนถึงเหตุการณ์ทางประวัติศาสตร์ในรัชสมัยของพระองค์อีกด้วย\r\nและเนื่องจากพระองค์ทรงทำคุณประโยชน์อันยิ่งใหญ่แก่ปวงชนชาวไทย\r\nจึงยกย่องให้พระองค์ทรงเป็น "มหาราช" พระองค์แรกแห่งประวัติศาสตร์ไทย</p>\r\n\r\n<p><br></p>', '0000-00-00'),
(31, 'อาณาจักรอยุธยา', 'ส 31101', '\r\n\r\n<p>อาณาจักรอยุธยา เป็นอาณาจักรของไทยในอดีต\r\nมีหลักฐานของการเป็นเมืองในลุ่มแม่น้ำเจ้าพระยา ตั้งแต่ราวพุทธศตวรรษที่ 16 - 18\r\nโดยมีร่องรอยของที่ตั้งเมือง โบราณสถาน โบราณวัตถุ\r\nและเรื่องราวเหตุการณ์ในลักษณะตำนาน พงศาวดารไปจนถึงศิลาจารึก<span>&nbsp;\r\nซึ่งถือว่าเป็นหลักฐานร่วมสมัยที่ใกล้เคียงเหตุการณ์มากที่สุด\r\nว่าก่อนการสถาปนากรุงศรีอยุธยา ใน พ.ศ. 1893&nbsp;\r\nนั้น ได้มีบ้านเมืองตั้งอยู่ก่อนแล้ว มีชื่อเรียกว่า เมืองอโยธยา หรือ\r\nอโยธยาศรีรามเทพนคร หรือ เมืองพระราม มีที่ตั้งอยู่บริเวณด้านตะวันออกของเกาะ\r\nเมืองอยุธยาเป็นเมืองที่มีความเจริญทางการเมืองการปกครอง&nbsp; และมีวัฒนธรรมที่รุ่งเรืองแห่งหนึ่ง&nbsp; มีการใช้กฎหมายในการปกครอง 3 ฉบับ คือ\r\nพระอัยการลักษณะเบ็ดเสร็จ พระอัยการลักษณะทาส และพระอัยการลักษณะกู้หนี้</span></p>\r\n\r\n<p>ประวัติศาสตร์</p>\r\n\r\n<p>สมเด็จพระรามาธิบดีที่ 1 หรือ พระเจ้าอู่ทอง\r\nทรงสถาปนากรุงศรีอยุธยาเป็นราชธานีเมื่อวันศุกร์ ขึ้น 6 ค่ำ เดือน 5 ปีขาล\r\nจุลศักราช 712[1] ตรงกับวันที่ 4 มีนาคม พ.ศ. 1893[2]\r\nกรุงศรีอยุธยาเป็นศูนย์กลางของอาณาจักรสยาม มีชื่อตามพงศาวดารว่า\r\nกรุงเทพมหานครบวรทวาราวดีศรีอยุธยามหาดิลกภพนพรัตน์ราชธานีบุรีรมย์อุดมพระราชนิเวศน์มหาสถาน\r\nดำรงมั่นคงสืบต่อยาวนานถึง 417 ปี จนถึงวันที่ 7 เมษายน พ.ศ. 2310\r\nมีประวัติในการปกครอง การกอบกู้อิสรภาพ วีรกรรม และขนบธรรมเนียมประเพณีมากมาย\r\nเป็นเมืองที่อุดมสมบูรณ์ด้วยพืชพันธ์ธัญญาหาร ดังคำกล่าวว่า "ในน้ำมีปลา\r\nในนามีข้าว" ทั่วทั้งจังหวัดพระนครศรีอยุธยายังมากไปด้วย วัดวาอาราม ปราสาท\r\nพระราชวัง ปูชนียสถาน และปูชนียวัตถุมากมาย</p>\r\n\r\n<p>อาณาจักรอยุธยามีพระมหากษัตริย์ปกครองสืบต่อกันมาถึง\r\n34 พระองค์<span>&nbsp; และมีพระราชวงศ์ผลัดเปลี่ยนกันครองรวม\r\n5 ราชวงศ์</span></p>\r\n\r\n<p>ราชวงศ์อู่ทอง มี 3 พระองค์ </p>\r\n\r\n<p>ราชวงศ์สุพรรณภูมิ มี 13 พระองค์ </p>\r\n\r\n<p>ราชวงศ์สุโขทัย มี 7 พระองค์ </p>\r\n\r\n<p>ราชวงศ์ปราสาททอง มี 4 พระองค์ </p>\r\n\r\n<p>ราชวงศ์บ้านพลูหลวง มี 6 องค์ </p>\r\n\r\n<p>กรุงศรีอยุธยาสูญเสียเอกราชแก่พม่า 2 ครั้ง\r\nครั้งแรกใน พ.ศ. 2112 สมเด็จพระนเรศวรมหาราช ทรงกู้เอกราชคืนมาได้ใน พ.ศ. 2127\r\nและเสียกรุงศรีอยุธยาครั้งที่ 2 ใน พ.ศ. 2310\r\nสมเด็จพระเจ้าตากสินมหาราชทรงกอบกู้เอกราชในปลายปีเดียวกัน\r\nแล้วทรงสถาปนากรุงธนบุรีเป็นราชธานีแห่งใหม่\r\nกวาดต้อนผู้คนจากกรุงศรีอยุธยาไปยังกรุงธนบุรีเพื่อสร้างบ้านเมืองแห่งใหม่ให้มั่นคง\r\nแต่กรุงศรีอยุธยาก็ไม่ได้กลายเป็นเมืองร้าง ยังมีคนที่รักถิ่นฐานบ้านเดิมอาศัยอยู่\r\nและมีราษฎรที่หลบหนี้ไปอยู่ตามป่ากลับเข้ามาอาศัยอยู่รอบ ๆ เมือง\r\nรวมกันเข้าเป็นเมืองจนทางการยกเป็นเมืองจัตวาเรียกว่า "เมืองกรุงเก่า"</p>\r\n\r\n<p>พระมหากษัตริย์และพระราชวงศ์ในสมัยกรุงศรีอยุธยา</p>\r\n\r\n<p>ลำดับ<span>&nbsp; &nbsp; พระนาม&nbsp; ปีที่ครองราชย์&nbsp; พระราชวงศ์</span></p>\r\n\r\n<p>1<span>&nbsp; สมเด็จพระรามาธิบดีที่\r\n1 (พระเจ้าอู่ทอง)</span></p>\r\n<p>1893 -1912 </p>\r\n\r\n<p>(19<span>&nbsp;\r\nปี)&nbsp; อู่ทอง</span></p>\r\n<p>2<span>&nbsp; สมเด็จพระราเมศวร\r\n(โอรสพระเจ้าอู่ทอง) ครองราชย์ครั้งที่ 1</span></p>\r\n<p>1912 - 1913 </p>\r\n\r\n<p>(1 ปี)<span>&nbsp; อู่ทอง</span></p>\r\n\r\n<p>3<span>&nbsp; สมเด็จพระบรมราชาธิราชที่\r\n1 (ขุนหลวงพะงั่ว)</span></p>\r\n<p>1913 - 1931 </p>\r\n\r\n<p>(18 ปี)<span>&nbsp; สุพรรณภูมิ</span></p>\r\n<p>4<span>&nbsp; สมเด็จพระเจ้าทองลัน\r\n(โอรสขุนหลวงพะงั่ว)</span></p>\r\n\r\n<p>1931 (7 วัน)<span>&nbsp; สุพรรณภูมิ</span></p>\r\n\r\n<p><span>&nbsp; สมเด็จพระราเมศวร\r\nครองราชย์ครั้งที่ 2&nbsp; </span></p>\r\n\r\n<p>1931 - 1938 </p>\r\n\r\n<p>(7 ปี)<span>&nbsp; อู่ทอง</span></p>\r\n\r\n<p>5<span>&nbsp; สมเด็จพระราชาธิราช\r\n(โอรสพระราเมศวร)</span></p>\r\n<p>1938 – 1952</p>\r\n\r\n<p>(14\r\nปี)<span>&nbsp; อู่ทอง</span></p>\r\n\r\n<p>6<span>&nbsp; </span></p>\r\n\r\n<p>สมเด็จพระอินราชาธิราช (เจ้านครอินทร์)\r\nโอรสพระอนุชาของขุนหลวงพระงั่ว<span>&nbsp; </span></p>\r\n\r\n<p>1952 - 1967 </p>\r\n\r\n<p>(16 ปี)<span>&nbsp; สุพรรณภูมิ</span></p>\r\n\r\n<p>7<span>&nbsp; สมเด็จพระบรมราชาธิราชที่\r\n2 (เจ้าสามพระยา) โอรสเจ้านครอินทร์</span></p>\r\n<p>1967 – 1991</p>\r\n\r\n<p>(16\r\nปี)<span>&nbsp; สุพรรณภูมิ</span></p>\r\n\r\n<p>8<span>&nbsp; สมเด็จพระบรมไตรโลกนาถ\r\n(โอรสเจ้าสามพระยา)</span></p>\r\n\r\n<p>1991 - 2031 </p>\r\n\r\n<p>(40 ปี)<span>&nbsp; สุพรรณภูมิ</span></p>\r\n\r\n<p>9<span>&nbsp; สมเด็จพระบรมราชาธิราชที่\r\n3 (โอรสพระบรมไตรโลกนาถ)</span></p>\r\n<p>2031 - 2034 </p>\r\n\r\n<p>(3 ปี)<span>&nbsp; สุพรรณถูมิ</span></p>\r\n\r\n<p>10<span>&nbsp; สมเด็จพระรามาธิบดีที่\r\n2 (โอรสพระบรมไตรโลกนาถ)</span></p>\r\n<p>2034 – 2072</p>\r\n\r\n<p>(38\r\nปี)<span>&nbsp; สุพรรณภูมิ</span></p>\r\n\r\n<p>11<span>&nbsp; สมเด็จพระบรมราชาธิราชที่\r\n4 (พระอนุชาต่างมารดาพระรามาธิบดีที่ 2)</span></p>\r\n<p>2072 - 2076 </p>\r\n\r\n<p>(4 ปี)<span>&nbsp; สุพรรณภูมิ</span></p>\r\n\r\n<p>12<span>&nbsp; สมเด็จพระรัษฎาธิราชกุมาร\r\n(โอรสพระบรมราชาธิราชที่ 4)</span></p>\r\n<p>2076 – 2077</p>\r\n\r\n<p>(1 ปี)<span>&nbsp; สุพรรณภูมิ</span></p>\r\n\r\n<p>13<span>&nbsp; สมเด็จพระไชยราชาธิราช\r\n(โอรสพระรามาธิบดีที่ 2)</span></p>\r\n<p>2077 – 2089</p>\r\n\r\n<p>(12\r\nปี)<span>&nbsp; สุพรรณภูมิ</span></p>\r\n\r\n<p>14<span>&nbsp; สมเด็จพระยอดฟ้า\r\n(พระแก้วฟ้า) (โอรสพระไชยราชาธิราช)</span></p>\r\n<p>2089 - 2091 </p>\r\n\r\n<p>(2 ปี)<span>&nbsp; สุพรรณภูมิ</span></p>\r\n\r\n<p>15<span>&nbsp; ขุนวรวงศาธิราช\r\n(สำนักประวัติศาสตร์บางแห่งไม่ยอมรับว่าเป็นกษัตริย์)</span></p>\r\n\r\n<p>2091 (42 วัน)<span>&nbsp; อู่ทอง</span></p>\r\n\r\n<p>16<span>&nbsp; สมเด็จพระมหาจักรพรรดิ\r\n(พระเฑียรราชา)</span></p>\r\n<p>2091 - 2111 </p>\r\n\r\n<p>(20 ปี)<span>&nbsp; สุพรรณภูมิ</span></p>\r\n\r\n<p>17<span>&nbsp; สมเด็จพระมหินทราธิราช\r\n(โอรสพระมหาจักรพรรดิ)</span></p>\r\n<p>2111 – 2112</p>\r\n\r\n<p>(1 ปี)<span>&nbsp; สุพรรณภูมิ</span></p>\r\n\r\n<p>18<span>&nbsp; สมเด็จพระมหาธรรมราชา\r\n(ราชบุตรเขยในพระมหาจักรพรรดิ์)</span></p>\r\n<p>2112 – 2133</p>\r\n\r\n<p>(21\r\nปี)<span>&nbsp; สุโขทัย</span></p>\r\n<p>19<span>&nbsp; </span></p>\r\n<p>สมเด็จพระนเรศวรมหาราช (โอรสพระมหาธรรมราชา)</p>\r\n<p>2133 – 2148</p>\r\n\r\n<p>(15\r\nปี)<span>&nbsp; </span></p>\r\n<p>สุโขทัย</p>\r\n\r\n<p>20<span>&nbsp; สมเด็จพระเอกาทศรถ\r\n(โอรสพระมหาธรรมราชา)</span></p>\r\n<p>2148 - 2153 </p>\r\n\r\n<p>(5 ปี)<span>&nbsp; สุโขทัย</span></p>\r\n\r\n<p>21<span>&nbsp; พระศรีเสาวภาคย์\r\n(โอรสพระเอกาทศรถ)</span></p>\r\n<p>2153 - 2154 </p>\r\n\r\n<p>(1 ปี)<span>&nbsp; สุโขทัย</span></p>\r\n\r\n<p>22<span>&nbsp; สมเด็จพระเจ้าทรงธรรม\r\n(โอรสพระเอกาทศรถ)</span></p>\r\n<p>2154 - 2171 </p>\r\n\r\n<p>(17 ปี)<span>&nbsp; สุโขทัย</span></p>\r\n\r\n<p>23<span>&nbsp; สมเด็จพระเชษฐาธิราช\r\n(โอรสพระเจ้าทรงธรรม)</span></p>\r\n\r\n<p>2172 (8 เดือน)<span>&nbsp; สุโขทัย</span></p>\r\n<p>24<span>&nbsp; </span></p>\r\n\r\n<p>สมเด็จพระอาทิตยวงศ์ (โอรสพระเจ้าทรงธรรม)</p>\r\n<p>2172 (28 วัน)<span>&nbsp; </span></p>\r\n\r\n<p>สุโขทัย</p>\r\n\r\n<p>25<span>&nbsp; สมเด็จพระเจ้าปราสาททอง\r\n(ออกญากลาโหมสุริยวงค์)</span></p>\r\n<p>2172 - 2199 </p>\r\n\r\n<p>(27 ปี)<span>&nbsp; ปราสาททอง</span></p>\r\n<p>26<span>&nbsp; สมเด็จเจ้าฟ้าไชย\r\n(โอรสพระเจ้าปราสาททอง)</span></p>\r\n\r\n<p>2199 (3 - 4 วัน)<span>&nbsp; ปราสาททอง</span></p>\r\n\r\n<p>27<span>&nbsp; พระศรีสุธรรมราชา\r\n(อนุชาพระเจ้าปราสาททอง)</span></p>\r\n\r\n<p>2199 (3 เดือน)<span>&nbsp; ปราสาททอง</span></p>\r\n\r\n<p>28<span>&nbsp; สมเด็จพระนารายณ์มหาราช\r\n(โอรสพระเจ้าปราสาททอง)</span></p>\r\n<p>2199 - 2231 </p>\r\n\r\n<p>(32 ปี)<span>&nbsp; ปราสาททอง</span></p>\r\n\r\n<p>29<span>&nbsp; สมเด็จพระเพทราชา</span></p>\r\n<p>2231 - 2246 </p>\r\n\r\n<p>(15 ปี)<span>&nbsp; </span></p>\r\n\r\n<p>บ้านพลูหลวง</p>\r\n<p>30<span>&nbsp; สมเด็จพระสรรเพชญ์ที่\r\n8 (พระเจ้าเสือ)</span></p>\r\n<p>2246 – 2251</p>\r\n\r\n<p>(6 ปี)<span>&nbsp; </span></p>\r\n\r\n<p>บ้านพลูหลวง</p>\r\n\r\n<p>31<span>&nbsp; สมเด็จพระเจ้าอยู่หัวท้ายสระ\r\n(โอรสพระเจ้าเสือ)</span></p>\r\n<p>2251 – 2275</p>\r\n\r\n<p>(24\r\nปี)<span>&nbsp; </span></p>\r\n\r\n<p>บ้านพลูหลวง</p>\r\n\r\n<p>32<span>&nbsp; สมเด็จพระเจ้าอยู่หัวบรมโกศ\r\n(โอรสพระเจ้าเสือ)</span></p>\r\n<p>2275 - 2301 </p>\r\n\r\n<p>(26 ปี)<span>&nbsp; </span></p>\r\n\r\n<p>บ้านพลูหลวง</p>\r\n\r\n<p>33<span>&nbsp; สมเด็จพระเจ้าอุทุมพร\r\n(โอรสพระเจ้าอยู่หัวบรมโกศ)</span></p>\r\n\r\n<p>2301 (2 เดือน)<span>&nbsp; </span></p>\r\n\r\n<p>บ้านพลูหลวง</p>\r\n\r\n<p>34<span>&nbsp; </span></p>\r\n\r\n<p>สมเด็จพระที่นั่งสุริยาสน์อมรินทร์\r\n(พระเจ้าเอกทัศน์) </p>\r\n\r\n<p>(โอรสพระเจ้าอยู่หัวบรมโกศ)<span>&nbsp; 2301 - 2310 </span></p>\r\n\r\n<p>(9 ปี)<span>&nbsp; </span></p>\r\n\r\n<p><br></p>', '0000-00-00'),
(32, 'สมัยกรุงธนบุรีและกรุงรัตนโกสินทร์', 'ส 31101', '<p>\r\n\r\n<p>ในปี พ.ศ. 2310-2325\r\nเริ่มต้นหลังจากที่สมเด็จพระเจ้ากรุงธนบุรีได้ขับไล่ทหารพม่าออกจากแผ่นดินไทย\r\nทำการรวมชาติ และได้ย้ายเมืองหลวงมาอยู่ที่กรุงธนบุรี โดยจัดตั้งการเมืองการปกครอง\r\nมีลักษณะการเมืองการปกครองยังคงดำรงไว้ซึ่งการเมืองการปกครองภายในสมัยอยุธยาอยู่ก่อน\r\nโดยมีพระมหากษัตริย์มีอำนาจเด็ดขาดในการเมืองการปกครอง อย่างไรก็ตาม\r\nภายหลังสิ้นรัชสมัยสมเด็จพระเจ้ากรุงธนบุรี\r\nพระบาทสมเด็จพระพุทธยอดฟ้าจุฬาโลกได้สถาปนาตนขึ้นเป็นพระมหากษัตริย์แห่งราชวงศ์จักรี\r\nและทรงย้ายเมืองหลวงมายังกรุงเทพมหานคร เริ่มยุคสมัยแห่งกรุงรัตนโกสินทร์</p>\r\n<p>ในช่วงคริสต์ทศวรรษ 1790\r\nกองทัพพม่าถูกขับไล่ออกจากดินแดนรัตนโกสินทร์อย่างถาวร\r\nและทำให้แคว้นล้านนาปลอดจากอิทธิพลของพม่าเช่นกัน\r\nโดยล้านนาถูกปกครองโดยราชวงศ์ที่นิยมราชวงศ์จักรีนับตั้งแต่นั้นเป็นต้นมา\r\nจนกระทั่งถูกผนวกเข้าเป็นส่วนหนึ่งของราชอาณาจักรสยามอย่างเป็นทางการ</p>\r\n<p>สมัยกรุงรัตนโกสินทร์ตอนต้น\r\nไทยยังเผชิญกับการรุกรานจากประเทศเพื่อนบ้าน ได้แก่ สงครามเก้าทัพ, สงครามท่าดินแดงกับพม่า\r\nตลอดจนกบฏเจ้าอนุวงศ์กับลาว และอานามสยามยุทธกับญวน</p>\r\n<p>ในช่วงนี้\r\nกรุงรัตนโกสินทร์ยังไม่ค่อยมีการติดต่อค้าขายกับชาติตะวันตกมากนัก\r\nต่อมาเมื่อชาวตะวันตกเริ่มเข้ามาค้าขายอีก\r\nได้ตระหนักว่าพวกพ่อค้าจีนได้รับสิทธิพิเศษเหนือคนไทยและพวกตน\r\nจึงได้เริ่มเรียกร้องสิทธิพิเศษต่าง ๆ มาโดยตลอด[15]\r\nมีการเดินทางเยือนของทูตหลายคน อาทิ จอห์น ครอเฟิร์ต\r\nตัวแทนจากบริษัทอินเดียตะวันออกของอังกฤษ[15]\r\nซึ่งเข้ามาในรัชสมัยพระบาทสมเด็จพระพุทธเลิศหล้านภาลัย แต่ยังไม่บรรลุข้อตกลงใด ๆ\r\nสนธิสัญญาที่มีการลงนามในช่วงนี้ เช่น สนธิสัญญาเบอร์นี และสนธิสัญญาโรเบิร์ต[15]\r\nแต่ก็เป็นเพียงข้อตกลงที่ไม่มีผลกระทบมากนัก\r\nและชาวตะวันตกไม่ค่อยได้รับสิทธิพิเศษเพิ่มมากขึ้นแต่อย่างใด</p>\r\n<p>อย่างไรก็ตาม\r\nได้มีคณะทูตตะวันตกเข้ามาเสนอสนธิสัญญาข้อตกลงทางการค้าอยู่เรื่อย ๆ\r\nเพื่อขอสิทธิทางการค้าให้เท่ากับพ่อค้าจีน\r\nและอังกฤษต้องการเข้ามาค้าฝิ่นอันได้กำไรมหาศาล[15] แต่ไม่ประสบความสำเร็จ ทั้งคณะของเจมส์\r\nบรุคจากอังกฤษ และโจเซฟ บัลเลสเตียร์จากสหรัฐอเมริกา\r\nทำให้ชาวตะวันตกขุ่นเคืองต่อราชสำนัก</p>\r\n\r\n\r\n\r\n<p>การเผชิญหน้ากับมหาอำนาจตะวันตก</p>\r\n\r\n<p>ภายหลังจากที่พม่าตกเป็นอาณานิคมของอังกฤษในปี\r\nพ.ศ. 2369\r\nพระมหากษัตริย์ไทยในรัชสมัยถัดมาจึงทรงตระหนักถึงภัยคุกคามที่มาจากชาติมหาอำนาจในทวีปยุโรป\r\nและพยายามดำเนินนโยบายทอดไมตรีกับชาติเหล่านั้น อย่างไรก็ตาม\r\nสยามมีการเปลี่ยนแปลงดินแดนหลายครั้ง\r\nรวมทั้งตกอยู่ในสถานะรัฐกันชนระหว่างอังกฤษและฝรั่งเศส ถึงกระนั้น\r\nสยามก็ไม่ตกเป็นอาณานิคมของชาติตะวันตก</p>\r\n<p>การปฏิวัติเปลี่ยนแปลงการปกครอง</p>\r\n\r\n<p>เมื่อวันที่ 24 มิถุนายน พ.ศ. 2475\r\nได้มีกลุ่มบุคคลที่เรียกว่า คณะราษฎร ได้ปฏิวัติเปลี่ยนแปลงรูปแบบการปกครอง\r\nจากสมบูรณาญาสิทธิราชมาเป็นราชาธิปไตยภายใต้รัฐธรรมนูญ\r\nเป็นเหตุการณ์ที่สร้างความเปลี่ยนแปลงทางการเมืองการปกครองแก่ประเทศไทยอย่างมาก\r\nและทำให้สถาบันพระมหากษัตริย์ที่เคยเป็นผู้ปกครองสูงสุดของประเทศมาช้านานต้องสูญเสียอำนาจส่วนใหญ่ไปในที่สุด\r\nโดยมีการร่างรัฐธรรมนูญฉบับ พ.ศ. 2475\r\nขึ้นเป็นรัฐธรรมนูญแห่งราชอาณาจักรไทยอย่างถาวรเป็นฉบับแรก</p>\r\n<p>ภายหลังจากเหตุการณ์เปลี่ยนแปลงดังกล่าว\r\nการต่อสู้ทางการเมืองยังคงมีการต่อสู้กันระหว่างผู้นำในระบอบสมบูรณาญาสิทธิราช\r\nกับระบอบใหม่ รวมทั้งความขัดแย้งในผู้นำระบอบใหม่ด้วยกันเอง\r\nโดยการต่อสู้ทางการเมืองและทางความคิดอุดมการณ์นี้ได้ดำเนินต่อเนื่องยาวนานเป็นเวลากว่า\r\n25 ปีภายหลังจากการปฏิวัติ และนำไปสู่ยุคตกต่ำของคณะราษฎรในกาลต่อมา ทำให้การเปลี่ยนแปลงการปกครองดังกล่าวถูกมองว่าเป็นพฤติการณ์\r\n"ชิงสุกก่อนห่าม"\r\nเนื่องจากชาวไทยยังไม่พร้อมสำหรับการปกครองในระบอบประชาธิปไตย\r\nอีกทั้งการปกครองในระยะแรกหลังการปฏิวัติยังคงอยู่ในระบอบเผด็จการทหาร</p>\r\n<p>สงครามโลกครั้งที่สอง</p>\r\n\r\n<p>ในระหว่างสงครามโลกครั้งที่สอง\r\nได้มีการเดินขบวนเรียกร้องให้รัฐบาลเอาดินแดนคืนของนิสิตนักศึกษา จอมพลแปลก\r\nพิบูลสงคราม จึงส่งทหารข้ามแม่น้ำโขงและรุกรานอินโดจีนฝรั่งเศส จนได้ดินแดนคืนมา 4\r\nจังหวัด ภายหลังการเข้าไกล่เกลี่ยของญี่ปุ่น โดยมีการรบที่เป็นที่รู้จักกันมาก\r\nได้แก่ การรบที่เกาะช้าง</p>\r\n<p>ต่อมา\r\nหลังจากการโจมตีกองทัพเรือสหรัฐอเมริกาที่เพิร์ลฮาร์เบอร์\r\nกองทัพญี่ปุ่นก็ได้รุกรานประเทศไทย โดยต้องการเคลื่อนทัพผ่านดินแดน\r\nรัฐบาลจอมพลแปลก พิบูลสงครามได้ดำเนินนโยบายเป็นพันธมิตรกับญี่ปุ่น\r\nรวมทั้งลงนามในสนธิสัญญาพันธมิตรทางการทหารกับญี่ปุ่น\r\nและประกาศสงครามต่อสหรัฐอเมริกาและสหราชอาณาจักร\r\nซึ่งนโยบายดังกล่าวของรัฐบาลถูกต่อต้านจากทั้งในและนอกประเทศ\r\nเนื่องจากไทยประสบกับภาวะเศรษฐกิจตกต่ำ</p>\r\n<p>หลังสงครามโลกครั้งที่สองสิ้นสุดลงในปี พ.ศ.\r\n2488 แม้ว่าประเทศไทยจะตกอยู่ในสถานะประเทศผู้แพ้สงคราม\r\nแต่เนื่องจากการเคลื่อนไหวของขบวนการเสรีไทย ทำให้ฝ่ายสัมพันธมิตรยอมรับ\r\nและไม่ถูกยึดครอง เพียงแต่ต้องคืนดินแดนระหว่างสงครามให้กับอังกฤษและฝรั่งเศส\r\nและจ่ายค่าเสียหายทดแทนเท่านั้น</p>\r\n<p>สงครามเย็น</p>\r\n\r\n<p>รัฐบาลไทยได้ดำเนินนโยบายเป็นพันธมิตรของสหรัฐอเมริกาในระหว่างสงครามเย็น\r\nดังจะเห็นได้จากนโยบายต่อต้านการขยายตัวของคอมมิวนิสต์ในคาบสมุทรอินโดจีน\r\nและยังส่งทหารไปร่วมรบกับฝ่ายพันธมิตร ได้แก่ สงครามเกาหลี และสงครามเวียดนาม</p>\r\n<p>ประเทศไทยประสบกับปัญหากองโจรคอมมิวนิสต์ในประเทศระหว่างคริสต์ทศวรรษ\r\n1960 และ 1970 อย่างไรก็ตาม\r\nปัญหาดังกล่าวก็ไม่ค่อยจะส่งผลกระทบต่อความมั่นคงของประเทศสักเท่าไหร่\r\nและกองโจรก็หมดไปในที่สุด</p>\r\n<p>การพัฒนาประชาธิปไตย</p>\r\n\r\n<p>หลังจากปัจจัยแวดล้อมด้านต่าง ๆ\r\nได้รับการพัฒนาขึ้น ประชาชนมีความพร้อมต่อการใช้อำนาจอธิปไตยเพิ่มมากขึ้น\r\nการเรียกร้องอำนาจอธิปไตยคืนจากฝ่ายทหารก็เกิดขึ้นเป็นระยะ กระทั่งในที่สุด\r\nภายหลังจากเหตุการณ์ 14 ตุลาคม พ.ศ. 2516\r\nฝ่ายทหารก็ไม่สามารถถือครองอำนาจอธิปไตยได้อย่างถาวรอีกต่อไป\r\nอำนาจอธิปไตยจึงได้เปลี่ยนไปอยู่ในมือของกลุ่มนักการเมือง\r\nซึ่งประกอบด้วยกลุ่มบุคคลสามกลุ่มหลัก คือ\r\nกลุ่มทหารที่เปลี่ยนบทบาทมาเป็นนักการเมือง กลุ่มนายทุนและผู้มีอิทธิพล\r\nและกลุ่มนักวาทศิลป์ แต่ต่อมาภายหลังจากการสิ้นสุดลงของยุคสงครามเย็น\r\nโลกได้เปลี่ยนมาสู่ยุคการแข่งขันกันทางการค้าซึ่งมีความรุนแรงเป็นอย่างมาก กลุ่มการเมืองที่มาจากกลุ่มทุนนิยมสมัยใหม่ได้เข้ามามีบทบาทแทน</p>\r\n\r\n<br></p>', '0000-00-00');

